import { useEffect, useState } from "react"
import { onSnapshot } from "firebase/firestore"
import { getCollectionRef } from "../../Services/CRUD"
import TicketCard from "../../Components/TicketCard"
import FinanceCard from "../../Components/FinanceCard"

const Dashboard = () => {
  const [listaTickets, setListaTickets] = useState([])
  const [fromDate, setFromDate] = useState("")
  const [toDate, setToDate] = useState("")

  useEffect(() => {
    const unSubscribe = onSnapshot((getCollectionRef("tickets")), (data) => {
      setListaTickets(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
  }, [])

 const uniqueBuildingsWithTickets = [
   ...new Set(listaTickets?.map((({property}) => property) ))
  ]

  // THIS PART WILL CHANGE INCOME TICKET AMOUNT ARRAY STRING TO INTERGER

  const incomeTypeTicketsAmount = []

  const incomeTypeTickets = listaTickets.filter(data => (data.type === 'income' && data.date >= fromDate && data.date <= toDate))
  const incomeTypeTicketsString = incomeTypeTickets.map(data => data.amount)

  incomeTypeTicketsString.forEach( str=> {
    incomeTypeTicketsAmount.push(Number(str))
  })
  let incomeSum = 0
  for (let i = 0; i < incomeTypeTicketsAmount.length; i++) {
    incomeSum += incomeTypeTicketsAmount[i];
  }
  // console.log(incomeTypeTickets)
  // console.log('incomeTypeTicketsAmount' + incomeTypeTicketsAmount)
  // console.log('This is incomeSum ' + incomeSum)
  // console.log('incomeTypeTickets' + incomeTypeTickets)

    // THIS PART WILL CHANGE INCOME TICKET AMOUNT ARRAY STRING TO INTERGER

    const expenseTypeTicketsAmount = []

    const expenseTypeTickets = listaTickets.filter(data => (data.type === 'expense' && data.date >= fromDate && data.date <= toDate))
    const expenseTypeTicketsString = expenseTypeTickets.map(data => data.amount)

    expenseTypeTicketsString.forEach( str=> {
      expenseTypeTicketsAmount.push(Number(str))
    })
    let expenseSum = 0
    for (let i = 0; i < expenseTypeTicketsAmount.length; i++) {
      expenseSum += expenseTypeTicketsAmount[i];
    }
    // console.log(expenseTypeTickets)
    // console.log('expenseTypeTicketsAmount' + expenseTypeTicketsAmount)
    // console.log('This is expenseSum ' + expenseSum)
    // console.log('expenseTypeTickets' + expenseTypeTickets)

  // THIS WILL GET THE COMISSION AND RESULT
    const porcentajeComision = 0.10
    const commission = incomeSum * porcentajeComision
    const result = incomeSum - expenseSum - commission


  const filterReset = () => {
      setFromDate("")
      setToDate("")
    }

  return (
    <div>
          <div className="flex flex-col w-full lg:flex-row justify-evenly mt-0 bg-pink-300">
            <h2 className="text-center text-xl font-bold lg:text-xl my-auto">Filtro por fechas</h2>
            <div className="flex w-full lg:w-3/4  justify-around">
            <div className="flex flex-col justify-center">
                <label htmlFor="fromDate" className="block text-gray-700 text-sm font-bold mb-2 text-2xl">Desde</label>
                                  <input 
                                  id="fromDate"
                                  name="fromDate"
                                  type="date"
                                  onChange={e => setFromDate(e.target.value)}
                                  value={fromDate}
                                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                  />
            </div>
            <div className="flex flex-col justify-center">
                <label htmlFor="toDate" className="block text-gray-700 text-sm font-bold mb-2 text-2xl">Hasta</label>
                                  <input 
                                  id="toDate"
                                  name="toDate"
                                  type="date"
                                  onChange={e => setToDate(e.target.value)}
                                  value={toDate}
                                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                  />
            </div>
            </div>
            <button onClick={() => filterReset()} className="bg-pink-500 hover:bg-white text-white hover:text-black font-semibold px-4 border border-black hover:border-pink-400 rounded shadow h-10 w-3/4 lg:w-48 mx-auto lg:mx-12 my-8">Borrar Filtros</button>
            </div> 

      <div className="font-style: italic w-full flex lg:flex-row flex-col lg:px-6 my-4">
          <div className="lg:w-2/4 w-full">
                <h1 className="font-bold text-2xl text-center text-pink-500">Gastos</h1>
                <div className="">
                  {uniqueBuildingsWithTickets?.map((uniqueBuilding, buildingIndex) => (
                    <div key={buildingIndex}>
                      <h3 className="font-bold text-xl ml-4">{uniqueBuilding}</h3>
                        {listaTickets.filter(data => data.property === uniqueBuilding && data.type === 'expense' && 
                        ( fromDate && toDate ?
                        (data.date >= fromDate && data.date <= toDate) : data.date))
                        .map((filteredBuilding, _index) => (
                          <TicketCard
                          id={_index}
                          data={filteredBuilding}
                          />
                          ))
                        }
                    </div>
                  ))}
                </div>
          </div>
            <div className="lg:w-2/4 w-full ">
              <h1 className="font-bold text-2xl text-center text-pink-500">Ingresos</h1>
              <div className=" w-full">
                  {uniqueBuildingsWithTickets?.map((uniqueBuilding, buildingIndex) => (
                    <div key={buildingIndex}>
                      <h3 className="font-bold text-xl ml-4">{uniqueBuilding}</h3>
                        {listaTickets.filter(data => data.property === uniqueBuilding && data.type === 'income' && 
                        ( fromDate && toDate ?
                        (data.date >= fromDate && data.date <= toDate) : data.date) )
                        .map((filteredBuilding, _index) => (
                          <TicketCard
                          id={_index}
                          data={filteredBuilding}
                          />
                          ))
                        }
                    </div>
                  ))}
                </div>
            </div>
      </div>
      <div>
        {fromDate&&toDate?
        <FinanceCard
        from={fromDate}
        to={toDate}
        income={incomeSum}
        expense={expenseSum}
        commission={commission}
        result={result}
        /> : 
        ""}
      </div>
    </div>
    )
  }

  export default Dashboard
