import { useState, useEffect } from "react"
import { onSnapshot } from "firebase/firestore"
import { addData, getCollectionRef } from "../../Services/CRUD"
import { useNavigate } from "react-router-dom"

const TicketPage = () => {
    const [propertyList, setPropertyList] = useState([])
    const navigate = useNavigate()

    const handleSubmit = async (e) => {
        e.preventDefault()
        await addData("tickets", {
            property: formData.property,
            subindex: formData.subindex,
            amount: formData.amount,
            type: formData.type,
            concept: formData.concept,
            timestamp: formData.timestamp,
        })
        navigate('/')
    }

    useEffect(() => {
        const unSubscribe = onSnapshot(getCollectionRef("tickets"), (data) => {
          setPropertyList(
            data.docs.map((doc) => {
              return {...doc.data(), id: doc.id}
            })
          )
        })
        return () => {
          unSubscribe()
        }
       }, [])

    const uniqueBuildings = [
        ...new Set(propertyList?.map(({property}) => property))
       ]

    const [formData, setFormData] = useState({
        timestamp: new Date().toISOString()
    })

    const editMode = false

    const handleChange = (e) => {
        const value = e.target.value
        const name = e.target.name

        setFormData((prevState) => ({
            ...prevState,
            [name]: value
        })
        )
    }
    console.log(formData)
    return (
        <div className="flex flex-col">
            <h1 className="text-black text-center">{editMode ? 'Update your Ticket' : 'Create a Ticket'}</h1>
            <div className="flex flex-col">
                <form onSubmit={handleSubmit} className="flex flex-col">
                    <section className="flex flex-col flex-wrap lg:flex-row w-3/4 m-10">
                            <label htmlFor="property">Property</label>
                            <select 
                                name="property"
                                Devalue={formData.property}
                                onChange={handleChange}
                                required={true}
                                >
                                    <>
                                    <option disabled selected value> -- select an option -- </option>
                                    {uniqueBuildings?.map((uniqueBuilding, buildingIndex) => (
                                    <option required={true} key={buildingIndex}>{uniqueBuilding}</option>
                                        ))}
                                    </>

                            </select>


                            <label htmlFor="subindex">Property Subindex</label>
                            <select 
                                name="subindex"
                                value={formData.subindex}
                                onChange={handleChange}
                                required={true}
                                >
                               {formData.property?
                               <>
                               <option disabled > -- select an option -- </option>
                                {propertyList.filter(expense => expense.property === formData.property)
                                .map((filteredBuilding, _index) => (
                                    console.log('this is filteredBuilding' + filteredBuilding),
                                 <option key={_index} > {filteredBuilding.subindex} </option>
                                    ))
                                }
                               </>
                               :""}

                            </select>

                            {/* <fieldset>
                                <legend>Select a Property Subindex</legend>

                                {propertyList.filter(expense => expense.property === formData.property)
                                .map((filteredBuilding, _index) => (
                                    <>
                                        <input type="checkbox" id={_index} value={formData.subindex} /> 
                                        <label htmlFor={_index} >{filteredBuilding.subindex}</label>
                                    </>
                                    ))
                                }


                            </fieldset> */}


                            <label htmlFor="type">Type</label>
                            <select 
                                name="type"
                                value={formData.type}
                                onChange={handleChange}
                                required={true}
                                >
                                <option disabled value> -- select an option -- </option>
                                <option value="expense">Expense</option>
                                <option value="income" >Income</option>
                            </select>
                            <label htmlFor="amount">Amount</label>
                            <input 
                            id="amount"
                            name="amount"
                            type="number"
                            onChange={handleChange}
                            required={true}
                            value={formData.amount}
                            />
                            <label htmlFor="concept">Concept</label>
                            <input 
                            id="concept"
                            name="concept"
                            type="text"
                            onChange={handleChange}
                            required={true}
                            value={formData.concept}
                            />
                        <input type="submit" />
                    </section>
                </form>
            </div>
        </div>
    )

}
export default TicketPage
