const Chart = ({buildingName, amount, dates, listaDeptos}) => {
    const handleChange = (e) => {
        const value = e.target.value
        const name = e.target.name

        setFormData((prevState) => ({
            ...prevState,
            [name]: value
        })
        )
    }
    return (
        <div className="h-80">
        <h2 className="text-3xl text-center font-bold">Chart</h2>
        <div className="flex flex-row justify-center justify-around">
            <label htmlFor="Building Name">Building Name</label>
            <select 
                                name="property"
                                value={formData.property}
                                onChange={handleChange}
                                required={true}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                                    <>
                                    <option disabled value={'disable-value'}> -- select an option -- </option>
                                    {listaDeptos?.map((uniqueBuilding, buildingIndex) => (
                                    <option key={buildingIndex}>{uniqueBuilding.property}</option>
                                        ))}
                                    </>
                                    
                            </select>
        </div>
        </div>
    )
}

export default Chart
