import { useEffect, useState } from "react"
import { onSnapshot } from "firebase/firestore"
import { getCollectionRef } from "../../Services/CRUD"
import TicketCard from "../../Components/TicketCard"
// import Chart from "../Chart"

const Dashboard = () => {
  const [listaTickets, setListaTickets] = useState([])
  const [buildingName, setBuildingName] = useState("")

  useEffect(() => {
    const unSubscribe = onSnapshot(getCollectionRef("tickets"), (data) => {
      setListaTickets(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
  }, [])

 const uniqueBuildings = [
   ...new Set(listaTickets?.map(({property}) => property))
  ]
  // console.log('this is unique buildings' + uniqueBuildings)

  const handleChange = (e) => {
    const value = e.target.value

    setBuildingName(() => ({
        propertyName: value
    })
    )
}

  const chartIncomeBuilding = listaTickets.filter(function(ticket){
    return (ticket.property === 'San Cosme' && ticket.type === 'income')
  })

  const amountIncomeArr = chartIncomeBuilding.map((property) => {
    return property.amount
  }) 

  const datesIncomeArr = chartIncomeBuilding.map((property) => {
    return property.date
  })  

  const chartExpenseBuilding = listaTickets.filter(function(ticket){
    return (ticket.property === 'San Cosme' && ticket.type === 'expense')
  })

  const amountExpenseArr = chartExpenseBuilding.map((property) => {
    return property.amount
  }) 

  const datesExpenseArr = chartExpenseBuilding.map((property) => {
    return property.date
  })  

  // console.log('this is chartBuilding', chartBuilding)
  // console.log('this is amount arr',amountIncomeArr)
  // console.log('this is dates arr',datesIncomeArr)

  return (
    <div>


      {/* <select 
      name="buildingName" 
      value={buildingName}
