import { Link } from 'react-router-dom'
// import PriorityDisplay from '../PriorityDisplay'
// import StatusDisplay from '../StatusDisplay'
import Bin from "../../multimedia/png/bin.png"
import { deleteData } from "../../Services/CRUD"

const TicketCard = ({expense}) => {
    const date = new Date().toDateString()
    console.log(expense)

    const handleDelete = async (id) => {
        await deleteData("tickets", id)
    }

    return (
        <div className='m-2 flex '>
        <Link to={`/ticket/${expense.id}`}>
            <div className='flex flex-row md:w-full m-1 justify-center w-full justify-between'>
                <h3 className='font-medium text-lg'>{expense.subindex}</h3>
                <p className='m-2'>${expense.amount}</p>
                 <p className='m-2'>{expense.concept}</p>
                 <p>{expense.date}</p>
            </div>
        </Link>
        <button onClick={() => handleDelete(expense.id)}>
            <img src={Bin} alt="delete icon" className="h-6" />
        </button>
        </div>
    )
}

export default TicketCard
