import { useState, useEffect } from "react"
import { onSnapshot } from "firebase/firestore"
import { addData, getDataById, updateData } from "../../Services/CRUD"
import { useNavigate, useParams } from "react-router-dom"
import { getCollectionRef, deleteData } from "../../Services/CRUD"

const PropertyPage = ({editMode}) => {
    const [propertyData, setPropertyData] = useState({})
    const navigate = useNavigate()
    let {id} = useParams()

    const handleSubmit = async (e) => {
        e.preventDefault()
        if(!editMode){
            await addData("properties", {
                property: propertyData.property,
                streetName: propertyData.streetName,
                streetNumber: propertyData.streetNumber,
                subindex: propertyData.subindex,
            })
            navigate('/')
        }
        if(editMode){
            await updateData("properties", {
                property: propertyData.property,
                streetName: propertyData.streetName,
                streetNumber: propertyData.streetNumber,
                subindex: propertyData.subindex,
            })
            navigate('/')
        }
    }

    const fetchData = async () => {
        const response = await getDataById("properties", id)
        setPropertyData({
            property: response.property,
            streetName: response.streetName,
            streetNumber: response.streetNumber,
            subindex: response.subindex,
        })
    }

    useEffect(() => {
        if(editMode) fetchData()
    }, [propertyData])

    useEffect(() => {
        const unSubscribe = onSnapshot(getCollectionRef("properties"), (data) => {
          setPropertyData(
            data.docs.map((doc) => {
              return {...doc.data(), id: doc.id}
            })
          )
        })
        return () => {
          unSubscribe()
        }
       }, [])


    const handleChange = (e) => {
        const value = e.target.value
        const name = e.target.name

        setPropertyData((prevState) => ({
            ...prevState,
            [name]: value
        })
        )
    }

    const handleDelete = async (id) => {
        await deleteData("properties", id)
    }
    
    return (
        <div className="flex flex-col">
            <h1 className="text-black text-center">{editMode ? 'Update your Property' : 'Create a Property'}</h1>
            <div className="flex flex-col">
                <form onSubmit={handleSubmit} className="bg-white shadow-md rounded pr-20 pt-6 pb-8 m-4 w-8/9">
                    <section className="flex flex-col w-full m-10">
                            
                                <label htmlFor="property" className="block text-gray-700 text-sm font-bold mb-2">Property</label>
                                <input 
                                id="property"
                                name="property"
                                type="text"
                                onChange={handleChange}
                                required={true}
                                value={propertyData.property}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                />
                           
                                <label htmlFor="streetName" className="block text-gray-700 text-sm font-bold mb-2">Street Name</label>
                                <input 
                                id="streetName"
                                name="streetName"
                                type="text"
                                onChange={handleChange}
                                required={true}
                                value={propertyData.streetName}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                />
                            
                                <label htmlFor="streetNumber" className="block text-gray-700 text-sm font-bold mb-2">Street Number</label>
                                <input 
                                id="streetNumber"
                                name="streetNumber"
                                type="number"
                                onChange={handleChange}
                                required={true}
                                value={propertyData.streetNumber}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                />
                           
                            <label htmlFor="subindex" className="block text-gray-700 text-sm font-bold mb-2">Interior Id</label>
                                    <input 
                                    id="subindex"
                                    name="subindex"
                                    type="text"
                                    onChange={handleChange}
                                    required={true}
                                    value={propertyData.subindex}
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    />
                                     <button> + Subindice </button>
                            
                
                        <input type="submit" className="bg-yellow-400 hover:bg-yellow-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline cursor-pointer mt-9" />

                        <button></button>
                    </section>
                </form>
            </div>
        </div>
    )
    
}
export default PropertyPage
