import React from 'react';
import { Bar } from 'react-chartjs-2'
import Chart from 'chart.js/auto'

const Charts = ({incomeDates, incomeAmount}) => {
    const data = {
        labels: [{incomeDates}],
        datasets: [
          {
            label: 'Iphone sales',
            data: [],
            fill: true,
            backgroundColor:"#2e4355",
            borderColor:"#2e4355",
          },
        ],
      };

      const options = {
        responsive: true,
        plugins: {
          legend: {
            position: 'top',
          },
          title: {
            display: true,
            text: 'Chart.js Line Chart',
          },
        },
      };

    return (
        <div className="h-80">
            <h2 className="text-3xl text-center font-bold">Chart</h2>
            <div className="flex flex-row justify-center justify-around">
            <Bar data={data} options={options}/>
            
            </div>
        </div>
    )
}

export default Charts
