import { useState, useEffect } from "react"
import { onSnapshot } from "firebase/firestore"
import { addData, deleteNumber, getDataById, updateData } from "../../Services/CRUD"
import { useNavigate, useParams } from "react-router-dom"
import { getCollectionRef, deleteData } from "../../Services/CRUD"

const PropertyPage = ({editMode}) => {
    const [propertyData, setPropertyData] = useState({})
    const navigate = useNavigate()
    let {id} = useParams()

    const handleSubmit = async (e) => {
        e.preventDefault()
        if(!editMode){
            await addData("properties", {
                property: propertyData.property,
                streetName: propertyData.streetName,
                streetNumber: propertyData.streetNumber,
                subindex: propertyData.subindex,
                subindex1: propertyData.subindex1,
                subindex2: propertyData.subindex2,
                subindex3: propertyData.subindex3,
                subindex4: propertyData.subindex4,
            })
            navigate('/')
        }
        if(editMode){
            await updateData("properties",id, {
                property: propertyData.property,
                streetName: propertyData.streetName,
                streetNumber: propertyData.streetNumber,
                subindex: propertyData.subindex,
                subindex1: propertyData.subindex1,
                subindex2: propertyData.subindex2,
                subindex3: propertyData.subindex3,
                subindex4: propertyData.subindex4,
            })
            navigate('/')
        }
    }

    const fetchData = async () => {
        const response = await getDataById("properties", id)
        setPropertyData({
            property: response.property,
            streetName: response.streetName,
            streetNumber: response.streetNumber,
            subindex: response.subindex,
            subindex1: response.subindex1,
            subindex2: response.subindex2,
            subindex3: response.subindex3,
            subindex4: response.subindex4,
        })
    }

    useEffect(() => {
        if(editMode) fetchData()
    }, [])

    useEffect(() => {
        const unSubscribe = onSnapshot(getCollectionRef("properties"), (data) => {
          setPropertyData(
            data.docs.map((doc) => {
              return {...doc.data(), id: doc.id}
            })
          )
        })
        return () => {
          unSubscribe()
        }
       }, [])


    const handleChange = (e) => {
        const value = e.target.value
        const name = e.target.name

        setPropertyData((prevState) => ({
            ...prevState,
            [name]: value
        })
        )
    }

    const handleDelete = async () => {
        await deleteData("properties", id)
        navigate('/')
    }
    
    // THIS WILL ONLY BE NEEDED IF WE NEED TO DELETE A FIELD, NEED TO MODIFY FIELD NAME IN CRUD
    // const handleDeleteField = async () => {
    //     await deleteNumber("properties", id)
    // }
    
    return (
        <div className="flex flex-col justify-center mx-auto">
            <h1 className="text-black text-center">{editMode ? 'Modifica los datos' : 'Crear una propiedad'}</h1>
            {/* {editMode ? <button className="bg-red-600 text-white" onClick={() => handleDeleteField(id)} >Eliminar Numero Interior</button> : ""} */}
            <div className="flex flex-col">
                <form onSubmit={handleSubmit} className="bg-white shadow-md rounded pr-20 pt-6 pb-8 m-4 w-8/9">
                    <section className="flex flex-col w-full m-10">
                            
                                <label htmlFor="property" className="block text-gray-700 text-sm font-bold mb-2">Nombre de la Propiedad</label>
                                <input 
                                id="property"
                                name="property"
                                type="text"
                                onChange={handleChange}
                                required={true}
                                value={propertyData.property}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                />
                           
                                <label htmlFor="streetName" className="block text-gray-700 text-sm font-bold mb-2">Nombre de la calle</label>
                                <input 
                                id="streetName"
                                name="streetName"
                                type="text"
                                onChange={handleChange}
                                required={true}
                                value={propertyData.streetName}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                />
                            
                                <label htmlFor="streetNumber" className="block text-gray-700 text-sm font-bold mb-2">Numero de la Calle</label>
                                <input 
                                id="streetNumber"
                                name="streetNumber"
                                type="number"
                                onChange={handleChange}
                                required={true}
                                value={propertyData.streetNumber}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                />
                           
                            <label htmlFor="subindex" className="block text-gray-700 text-sm font-bold mb-2">Numero Interior</label>
                                    <input 
                                    id="subindex"
                                    name="subindex"
                                    type="text"
                                    onChange={handleChange}
                                    required={true}
                                    value={propertyData.subindex}
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    />
                            {propertyData.subindex?
                            <>
                            <label htmlFor="subindex1" className="block text-gray-700 text-sm font-bold mb-2">Numero Interior 2</label>
                                    <input 
                                    id="subindex1"
                                    name="subindex1"
                                    type="text"
                                    onChange={handleChange}
                                    value={propertyData.subindex1}
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    />
                             
                            </> : ""   
                        }

                            {propertyData.subindex1?
                            <>
                            <label htmlFor="subindex2" className="block text-gray-700 text-sm font-bold mb-2">Numero Interior 3</label>
                                    <input 
                                    id="subindex2"
                                    name="subindex2"
                                    type="text"
                                    onChange={handleChange}
                                    value={propertyData.subindex2}
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    />
                            </> : ""   
                        }

                            {propertyData.subindex2?
                            <>
                            <label htmlFor="subindex3" className="block text-gray-700 text-sm font-bold mb-2">Numero Interior 4</label>
                                    <input 
                                    id="subindex3"
                                    name="subindex3"
                                    type="text"
                                    onChange={handleChange}
                                    value={propertyData.subindex3}
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    />
                            </> : ""   
                        }

                            {propertyData.subindex3?
                            <>
                            <label htmlFor="subindex4" className="block text-gray-700 text-sm font-bold mb-2">Numero Interior 5</label>
                                    <input 
                                    id="subindex4"
                                    name="subindex4"
                                    type="text"
                                    onChange={handleChange}
                                    value={propertyData.subindex4}
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    />
                            </> : ""   
                        }
                            
                            
                            
                
                        <input type="submit" value="Enviar" className="bg-yellow-400 hover:bg-yellow-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline cursor-pointer mt-9" />
                        {editMode ? <button className="bg-red-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline cursor-pointer mt-9 my-8" onClick={() => handleDelete(id)} >Eliminar Propiedad</button> : ""}
                    </section>
                </form>
            </div>
        </div>
    )
    
}

export default PropertyPage
