import React from 'react';
import { Bar } from 'react-chartjs-2'
import Chart from 'chart.js/auto'

const Charts = ({incomeDates, incomeAmount, expenseDates, expenseAmount}) => {
    const data = {
        labels: incomeDates,
        datasets: [
          {
            id: 1,
            label: 'Income',
            data: incomeAmount,
            fill: true,
            backgroundColor:"#2e4355",
            pointBorderColor:"#8884d8",
            pointBorderWidth:5,
            pointRadius:8,
            tension: 0.4
          },
          {
            id: 2,
            label: 'Expense',
            data: expenseAmount,
            fill: true,
            backgroundColor:"#2e4155",
            pointBorderColor:"#8484d8",
            pointBorderWidth:5,
            pointRadius:8,
            tension: 0.4
          },
        ],
      };

      const options = {
        plugins:{legend:{display:false}},
        layout:{padding:{bottom:100}},
        scales: {
          y:{
            ticks:{
              color:"black",
              font:{
                size:32
              }
            },
            grid:{
              color:"#243240"
            }
          },
          x:{
            ticks:{
              color:"black",
              font:{
                size:32
              }
            }
          }
        },
      };

    return (
        <div className="h-1/5 mb-5 mx-10">
            <h2 className="text-3xl text-center font-bold">Chart for {buildingName}</h2>
            <div className="flex flex-row justify-center justify-around">
            <Bar 
            datasetIdKey='id'
            data={data} 
            options={options}
            />
            
            </div>
        </div>
    )
}

export default Charts
