import React from 'react';
import { Document, Page, Text, PDFDownloadLink } from '@react-pdf/renderer';


const TicketPDF = ({clientDetails, formData, objetoEnviado}) => {
  return (
    <Document>
        <Page>
        
        <Text>{`Remision: ${formData.ticketNumber}`}</Text>
        <Text>{`Nombre del cliente: ${formData.clientName}`}</Text>
        <Text>{`Direccion de envio: ${clientDetails.direction}`}</Text>
        <Text>{`Direccion de envio: ${clientDetails.phone}`}</Text>
        <Text>Detalle de productos:</Text>
      {objetoEnviado.map((objeto, index) => (
        <div key={index}>
          {/* Muestra los detalles de cada objeto del array */}
          <Text>{`Producto ${index + 1}`}</Text>
          {Object.entries(objeto).map(([key, value], i) => (
            <Text key={i}>{`${key}: ${value}`}</Text>
          ))}
        </div>
      ))}
        </Page>
    </Document>
  )
}

export default TicketPDF
