import { useEffect, useState } from "react"
import { onSnapshot } from "firebase/firestore"
import { getCollectionRef } from "../../Services/CRUD"
import { useNavigate } from "react-router-dom"


const SideBar = () => {
  const [listaDeptos, setListaDeptos] = useState([])
  const navigate = useNavigate()
  // date = new Date().toLocaleDateString()

  useEffect(() => {
    const unSubscribe = onSnapshot(getCollectionRef("expenses"), (data) => {
      setListaDeptos(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
   }, [])

   const uniqueBuildings = [
    ...new Set(listaDeptos?.map(({property}) => property))
   ]

  return (<aside className="w-1/5 mt-4">
        <button onClick={() => navigate('/')} className="bg-white hover:bg-yellow-200 text-gray-800 font-semibold py-2 px-4 border border-gray-400 rounded shadow ml-8 w-3/4"> Dashboard </button>
        <div>
        <h1 className="font-bold text-lg text-center my-6">Properties</h1>
        {uniqueBuildings?.map((uniqueBuilding, buildingIndex) => (
                <div key={buildingIndex} className="my-">
                  <button className="bg-white hover:bg-yellow-200 text-gray-800 font-semibold py-2 px-4 border border-gray-400 rounded shadow ml-8 w-3/4">{uniqueBuilding}</button>
                </div>
              ))}
            </div>
      </aside>)
    }

export default SideBar
