// FinanceCard.jsx

import React from "react";

const FinanceCard = ({ totalExpenses, totalIncome, balance }) => {
  const balanceColor = balance === 0 ? "black" : balance > 0 ? "green" : "red";

  return (
    <div className="border border-gray-500 p-4 max-w-md mx-auto">
      <h2 className="text-center font-bold text-xl mb-4">Resumen Financiero</h2>
      <table className="border-collapse w-full">
        <thead>
          <tr>
            <th className="border border-gray-500">Gastos</th>
            <th className="border border-gray-500">Ingresos</th>
            <th className="border border-gray-500">Balance</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td className="border border-gray-500 text-center">{totalExpenses}</td>
            <td className="border border-gray-500 text-center">{totalIncome}</td>
            <td className={`border border-gray-500 text-center text-${balanceColor}`}>{balance}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default FinanceCard;
