import { useState, useEffect } from "react"
import { onSnapshot } from "firebase/firestore"
import { getCollectionRef } from "../../Services/CRUD"

const TicketPage = () => {
    const [listaDeptos, setListaDeptos] = useState([])

    useEffect(() => {
        const unSubscribe = onSnapshot(getCollectionRef("tickets"), (data) => {
          setListaDeptos(
            data.docs.map((doc) => {
              return {...doc.data(), id: doc.id}
            })
          )
        })
        return () => {
          unSubscribe()
        }
       }, [])

    const uniqueBuildings = [
        ...new Set(listaDeptos?.map(({property}) => property))
       ]

    const [formData, setFormData] = useState({
        timestamp: new Date().toISOString()
    })

    const editMode = false

    const handleSubmit = () => {
        console.log('submitted')
    }
    const handleChange = (e) => {
        const value = e.target.value
        const name = e.target.name

        setFormData((prevState) => ({
            ...prevState,
            [name]: value
        })
        )
    }
    console.log(formData)
    console.log(setListaDeptos)
    console.log(uniqueBuildings)
    return (
        <div>
            <h1>{editMode ? 'Update your Ticket' : 'Create a Ticket'}</h1>
            <div className="flex">
                <form onSubmit={handleSubmit}>
                    <section>
                            <label htmlFor="property">Property</label>
                            <select 
                                name="property"
                                value={formData.property}
                                onChange={handleChange}
                                >
                                {uniqueBuildings?.map((uniqueBuilding, buildingIndex) => (
                                        <option required={true} key={buildingIndex}>{uniqueBuilding}</option>
                                    ))}

                            </select>
                            <label htmlFor="subindex">Property Subindex</label>
                            <select 
                                name="subindex"
                                value={formData.subindex}
                                onChange={handleChange}
                                >
                               {uniqueBuildings?.map((uniqueBuilding, buildingIndex) => (
                                    <div key={buildingIndex}>
                                    <h3 className="font-bold text-xl">{uniqueBuilding}</h3>
                                        {listaDeptos.filter(expense => expense.property === uniqueBuilding && expense.type === 'expense')
                                        .map((filteredBuilding, _index) => (
                                        <option
                                        id={_index}
                                        expense={filteredBuilding}
                                        >
                                        {filteredBuilding.subindex}
                                        </option>
                                        ))
                                        }
                                    </div>
                                ))}

                            </select>
                            <label htmlFor="type">Type</label>
                            <select 
                                name="type"
                                value={formData.type}
                                onChange={handleChange}
                                required={true}
                                >
                                <option value="expense">Expense</option>
                                <option value="income" selected>Income</option>
                            </select>
                            <label htmlFor="amount">Amount</label>
                            <input 
                            id="amount"
                            name="amount"
                            type="number"
                            onChange={handleChange}
                            required={true}
                            value={formData.amount}
                            />
                            <label htmlFor="concept">Concept</label>
                            <input 
                            id="concept"
                            name="concept"
                            type="text"
                            onChange={handleChange}
                            required={true}
                            value={formData.concept}
                            />

                    </section>
                </form>
            </div>
        </div>
    )

}
export default TicketPage
