import { Line } from 'react-chartjs-2'

const Chart = ({incomeDates, incomeAmount, expenseDates, expenseAmount  }) => {
    const data = {
        labels: [{incomeDates}],
        datasets: [
            {
                label: 'Income',
                data: [{incomeAmount}],
                backgroundColor: 'rgb(255,99,132)',
                borderColor: 'rgb(255,99,132)'
            }
        ]
    }

    const options = {
        scales: {
            yAxes: [
                {
                    ticks: {
                        beginAtZero: true, 
                    }
                }
            ]
        }
    }

    return (
        <div className="h-80">
            <h2 className="text-3xl text-center font-bold">Chart</h2>
            <div className="flex flex-row justify-center justify-around">
            <canvas id="myChart" width="400" height="400"></canvas>
            <Line data={data} options={options}/>

            
            </div>
        </div>
    )
}

export default Chart
