import { useEffect, useState } from "react"
import { onSnapshot } from "firebase/firestore"
import { getCollectionRef } from "../../Services/CRUD"
import TicketCard from "../../Components/TicketCard"
import Chart from "../Chart"

const Dashboard = () => {
  const [listaDeptos, setListaDeptos] = useState([])
  const [buildingName, setBuildingName] = useState("")

  useEffect(() => {
    const unSubscribe = onSnapshot(getCollectionRef("tickets"), (data) => {
      setListaDeptos(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
  }, [])

 const uniqueBuildings = [
   ...new Set(listaDeptos?.map(({property}) => property))
  ]

  const handleChange = (e) => {
    const value = e.target.value
    const name = e.target.name

    setBuildingName((prevState) => ({
        ...prevState,
        [name]: value
    })
    )
}


  console.log('lista deptos',listaDeptos)

  const chartBuilding = listaDeptos.filter(function (properties){
    return 
  })

  const amountArr = listaDeptos.map((property) => {
    return property.amount
  }) 

  const datesArr = listaDeptos.map((property) => {
    return property.date
  })  




  console.log('this is buildingName', buildingName)
  console.log('this is chartBuilding', chartBuilding)
  console.log('this is amount arr',amountArr)
  console.log('this is dates arr',datesArr)

  return (
    <div>


      <select 
      name="buildingName" 
      value={buildingName}
      onChange={handleChange}
      required={true}
      defaultValue={'disable-value'}
      className="shadow appearance-none border rounded w-2/4 py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
      >
        <>
        <option disabled value={'disable-value'}> -- select an option -- </option>
        {listaDeptos?.map((uniqueBuilding, buildingIndex) => (
        <option key={buildingIndex}>{uniqueBuilding.property}</option>
        ))}
        </>
      </select>




    <Chart
    dates={datesArr}
    amount={amountArr}
    />


   <div className="font-style: italic w-full flex flex-wrap">
      <div className="w-2/4">
            <h1 className="font-bold text-2xl text-center">Gastos</h1>
            <div className="">
              {uniqueBuildings?.map((uniqueBuilding, buildingIndex) => (
                <div key={buildingIndex}>
                  <h3 className="font-bold text-xl">{uniqueBuilding}</h3>
                    {listaDeptos.filter(expense => expense.property === uniqueBuilding && expense.type === 'expense')
                    .map((filteredBuilding, _index) => (
                      <TicketCard
                      id={_index}
                      expense={filteredBuilding}
                      />
                      ))
                    }
                </div>
              ))}
            </div>
      </div>
        <div className="w-2/4">
          <h1 className="font-bold text-2xl text-center">Ingresos</h1>
          <div className="w-full">
              {uniqueBuildings?.map((uniqueBuilding, buildingIndex) => (
                <div key={buildingIndex}>
                  <h3 className="font-bold text-xl">{uniqueBuilding}</h3>
                    {listaDeptos.filter(expense => expense.property === uniqueBuilding && expense.type === 'income')
                    .map((filteredBuilding, _index) => (
                      <TicketCard
                      id={_index}
                      color={filteredBuilding.color}
                      expense={filteredBuilding}
                      />
                      ))
                    }
                </div>
              ))}
            </div>
        </div>
    </div>
              </div>
    )
  }

  export default Dashboard
