import { Line } from 'react-chartjs-2'

const Chart = ({incomeDates, incomeAmount, expenseDates, expenseAmount  }) => {
    const data = {
        labels: ['Jan', 'Mar', 'May', 'July', 'Oct'],
        datasets: [
          {
            label: 'Iphone sales',
            data: [400, 1000, 4000, 800, 1500],
            fill: true,
            backgroundColor:"#2e4355",
            pointBorderColor:"#8884d8",
            pointBorderWidth:5,
            pointRadius:8,
            tension: 0.4
          },
        ],
      };

    const options = {
        plugins:{legend:{display:false}},
        layout:{padding:{bottom:100}},
        scales: {
          y:{
            ticks:{
              color:"white",
              font:{
                size:18
              }
            },
            grid:{
              color:"#243240"
            }
          },
          x:{
            ticks:{
              color:"white",
              font:{
                size:18
              }
            }
          }
        },
      };

    return (
        <div className="h-80">
            <h2 className="text-3xl text-center font-bold">Chart</h2>
            <div className="flex flex-row justify-center justify-around">
            <canvas id="myChart" width="400" height="400"></canvas>
            <Line data={data} options={options}/>

            
            </div>
        </div>
    )
}

export default Chart
