import { BrowserRouter, Route, Routes } from "react-router-dom"
import NavBar from "../../Components/NavBar/index"
import SideBar from "../../Components/Aside"
import Dashboard from "../Dashboard"
import TicketPage from "../TicketPage"
import PropertyPage from "../PropertyPage"
import "./style.css"
import { useEffect, useState } from "react"
import { onSnapshot } from "firebase/firestore"
import { getCollectionRef } from "../../Services/CRUD"

const HomePage = () => {
  const [listaDeptos, setListaDeptos] = useState([])
  const [listaTickets, setListaTickets] = useState([])

  useEffect(() => {
    const unSubscribe = onSnapshot(getCollectionRef("properties"), (data) => {
      setListaDeptos(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
   }, [])


  useEffect(() => {
    const unSubscribe = onSnapshot(getCollectionRef("tickets"), (data) => {
      setListaTickets(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
  }, [])

  // const allBuildingList = [
  //   ...new Set(listaDeptos?.map(({property}) => property))
  //  ]

  const allBuildingList = {listaDeptos}

  // const uniqueBuildingsWithTickets = [
  //   ...new Set(listaTickets?.map(({property}) => property))
  //  ]


  return (
          <BrowserRouter>
            <NavBar/>
          <div className="main">
            <SideBar/>
            <div className="w-full">
              <Routes>
                <Route path="/" element={<Dashboard/>}/>
                <Route path="/ticket" element={<TicketPage/>}/>
                <Route path="/ticket/:id" element={<TicketPage editMode={true}/>}/>
                <Route path="/property" element={<PropertyPage/>}/>
                <Route path="/property/:id" element={<PropertyPage editMode={true}/>}/>
              </Routes>
            </div>
          </div>
          </BrowserRouter>
          )
        }

export default HomePage
