import { useState, useEffect, useContext} from "react"
import { userContext } from "../../context/userProvider"
import { addData, getData, getDataById, updateData } from "../../Services/CRUD"
import { useNavigate } from "react-router-dom"
import { useParams } from "react-router-dom"

const getNextConsecutiveNumber = async () => {
    // Obtener el año actual
    const currentYear = new Date().getFullYear();

    // Obtener todos los tickets del año actual
    const tickets = await getData("tickets");

    // Filtrar los tickets del año actual
    const currentYearTickets = tickets.filter(
        (ticket) => ticket.timestamp && new Date(ticket.timestamp).getFullYear() === currentYear
    );

    console.log('currentYearTickets'+ currentYearTickets)

    // Obtener el número máximo actual de tickets
    let maxNumber = 0;
    if (currentYearTickets.length > 0) {
        maxNumber = currentYearTickets.reduce((max, ticket) => {
            if (ticket.id) {
                const ticketNumber = parseInt(ticket.id.substring(4), 10); // Obtener el número de ticket
                return ticketNumber > max ? ticketNumber : max;
            }
            return max;
        }, 0);
    }

    // Generar el siguiente número consecutivo
    const nextNumber = maxNumber + 1;

    // Formatear el número a 3 dígitos (añadiendo ceros a la izquierda si es necesario)
    const formattedNumber = nextNumber.toString().padStart(3, "0");

    // Construir el ID del ticket (AAMM###)
    const ticketID = `${currentYear}${formattedNumber}`;

    console.log(ticketID)
    return ticketID;
};



const TicketPage = ({editMode}) => {
    const user = useContext(userContext)
    const navigate = useNavigate()
    const [formData, setFormData] = useState({
        timestamp: new Date().toISOString(),
        ticketNumber: '' // Nuevo campo para el número consecutivo
    })
    let {id} = useParams()
    
    // Obtener el próximo número consecutivo al montar el componente
        useEffect(() => {
            const fetchNextTicketID = async () => {
            const nextID = await getNextConsecutiveNumber();
            setFormData((prevState) => ({ ...prevState, id: nextID }));
            };

            fetchNextTicketID();
        }, []);

        const handleSubmit = async (e) => {
            e.preventDefault();
            if (!editMode) {
              await addData("tickets", {
                ticketNumber: formData.ticketID, // Utilizar el ID generado consecutivo
                amount: formData.amount,
                type: formData.type,
                category: formData.category,
                concept: formData.concept,
                timestamp: formData.timestamp,
                date: formData.date,
                uid: user.uid,
              });
              navigate("/");
            }
        if (editMode){
            await updateData("tickets", id, {
                ticketNumber: formData.ticketID,
                amount: formData.amount,
                type: formData.type,
                category: formData.category,
                concept: formData.concept,
                timestamp: formData.timestamp,
                date: formData.date,
                uid: user.uid,
            })
            navigate('/')
        }
    }

    const fetchData = async () => {
       const response =  await getDataById("tickets", id)
        setFormData({
            amount: response.amount,
            type: response.type,
            category: response.category,
            concept: response.concept,
            timestamp: response.timestamp,
            date: response.date,
            uid: user.uid
            })
    }

    useEffect(() => {
        if(editMode) fetchData()
    }, [])

    const handleChange = (e) => {
        const value = e.target.value;
        const name = e.target.name;
    
        setFormData((prevState) => ({
            ...prevState,
            [name]: value
        }));
    }
    return (
        <div className="flex flex-col">
            <h1 className="text-black text-center">{editMode ? 'Actualiza tu movimiento' : 'Crear un movimiento'}</h1>
            <div className="flex flex-col">
                <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 m-4 w-3/4">
                    <section className="flex flex-col flex-wrap w-3/4 m-10">
                      <label htmlFor="type"className="block text-gray-700 text-sm font-bold mb-2">Tipo de Movimiento</label>
                            <select 
                                name="type"
                                value={formData.type}
                                onChange={handleChange}
                                required={true}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                                <option value={'disable-value'} disabled> -- Selecciona una opcion -- </option>
                                <option value="expense">Gasto / Egreso</option>
                                <option value="income" >Ingreso</option>
                            </select>
                            <label htmlFor="category"className="block text-gray-700 text-sm font-bold mb-2">Categoria</label>
                            <select 
                                name="category"
                                value={formData.category}
                                onChange={handleChange}
                                required={true}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                                <option value={'disable-value'} disabled> -- Selecciona una opcion -- </option>
                                <option value="shared">Familia</option>
                                <option value="personal" >Personal</option>
                                <option value="negocio" >Negocio</option>
                            </select>
                            <label htmlFor="amount" className="block text-gray-700 text-sm font-bold mb-2">Monto</label>
                            <input 
                            id="amount"
                            name="amount"
                            type="number"
                            onChange={handleChange}
                            required={true}
                            value={formData.amount}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                            <label htmlFor="concept" className="block text-gray-700 text-sm font-bold mb-2">Concepto</label>
                            <input 
                            id="concept"
                            name="concept"
                            type="text"
                            onChange={handleChange}
                            required={true}
                            value={formData.concept}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                            <label htmlFor="date" className="block text-gray-700 text-sm font-bold mb-2">Fecha</label>
                            <input 
                            id="date"
                            name="date"
                            type="date"
                            onChange={handleChange}
                            required={true}
                            value={formData.date}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                        <input value="Enviar" type="submit" className="bg-yellow-400 hover:bg-yellow-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline cursor-pointer mt-9" />
                    </section>
                </form>
            </div>
        </div>
    )
    
}
export default TicketPage
