import { useEffect, useState, useContext } from "react";
import { onSnapshot } from "firebase/firestore";
import { getCollectionRef } from "../../Services/CRUD";
import TicketCard from "../../Components/TicketCard";
import FinanceCard from "../../Components/FinanceCard";
import { userContext } from "../../context/userProvider";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [listaTickets, setListaTickets] = useState([]);
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [sharedTicket, setSharedTicket] = useState("shared"); // Corregido el nombre de la variable
  const user = useContext(userContext)
  const navigate = useNavigate()

  useEffect(() => {
    const unSubscribe = onSnapshot(getCollectionRef("tickets"), (data) => {
      setListaTickets(
        data.docs.map((doc) => {
          return { ...doc.data(), id: doc.id };
        })
      );
    });

    return () => {
      unSubscribe();
    };
  }, []);

  // Obtener los tickets filtrados por los criterios seleccionados
  const filteredExpenses = listaTickets.filter((data) => {
    if (sharedTicket === "personal") {
      return (
        data.type === "expense" &&
        data.category === sharedTicket &&
        data.uid === user.uid
      );
    } else if (sharedTicket === "negocio") {
      return (
        data.type === "expense" &&
        data.category === sharedTicket &&
        data.uid === user.uid
      );
    } else {
      return (
        data.type === "expense" &&
        data.category === sharedTicket &&
        (fromDate && toDate ? (data.date >= fromDate && data.date <= toDate) : data.date)
      );
    }
  });
  
  const filteredIncome = listaTickets.filter((data) => {
    if (sharedTicket === "personal") {
      return (
        data.type === "income" &&
        data.category === sharedTicket &&
        data.uid === user.uid
      );
    } else if (sharedTicket === "negocio") {
      return (
        data.type === "income" &&
        data.category === sharedTicket &&
        data.uid === user.uid
      );
    } else {
      return (
        data.type === "income" &&
        data.category === sharedTicket &&
        (fromDate && toDate ? (data.date >= fromDate && data.date <= toDate) : data.date)
      );
    }
  });
  
  
  // THIS PART WILL CHANGE INCOME TICKET AMOUNT ARRAY STRING TO INTERGER

  const incomeTypeTicketsAmount = []

  const incomeTypeTickets = listaTickets.filter(data => (data.type === 'income' && data.date >= fromDate && data.date <= toDate))
  const incomeTypeTicketsString = incomeTypeTickets.map(data => data.amount)
  
  incomeTypeTicketsString.forEach( str=> {
    incomeTypeTicketsAmount.push(Number(str))
  })
  let incomeSum = 0
  for (let i = 0; i < incomeTypeTicketsAmount.length; i++) {
    incomeSum += incomeTypeTicketsAmount[i];
  }
  
    // THIS PART WILL CHANGE INCOME TICKET AMOUNT ARRAY STRING TO INTERGER

    const expenseTypeTicketsAmount = []

    const expenseTypeTickets = listaTickets.filter(data => (data.type === 'expense' && data.date >= fromDate && data.date <= toDate))
    const expenseTypeTicketsString = expenseTypeTickets.map(data => data.amount)
    
    expenseTypeTicketsString.forEach( str=> {
      expenseTypeTicketsAmount.push(Number(str))
    })
    let expenseSum = 0
    for (let i = 0; i < expenseTypeTicketsAmount.length; i++) {
      expenseSum += expenseTypeTicketsAmount[i];
    }
    
  // THIS WILL GET THE COMISSION AND RESULT
    const porcentajeComision = 0.10
    const commission = incomeSum * porcentajeComision
    const result = incomeSum - expenseSum - commission

  
    // Obtener la suma total de los gastos y los ingresos
    const totalExpenses = filteredExpenses.reduce(
      (total, ticket) => total + Number(ticket.amount),
      0
    );
    
    const totalIncome = filteredIncome.reduce(
      (total, ticket) => total + Number(ticket.amount),
      0
    );
  
    const balance = totalIncome - totalExpenses;
  
    const filterReset = () => {
      setFromDate("");
      setToDate("");
    };

  return (
    <div>
          {/* AGREGAR MOVIMIENTO BTN */}
          <div className="mx-auto flex my-4">
              <button className="font-bold border rounded-md w-48 h-16 bg-yellow-500 mx-auto" onClick={() => navigate('/ticket')}> Agregar Remision </button>
          </div>
          {/* FILTER SEARCH */}
          <div>
            <h2 className="text-center text-xl font-bold lg:text-xl my-auto">Filtro por fechas</h2>
            <div className="flex flex-col w-full lg:flex-row justify-evenly mt-0 border-yellow-300 ">
              <div className="flex w-full lg:w-3/4  justify-around">
              <div className="flex flex-col justify-center">
                  <label htmlFor="fromDate" className="block text-gray-700 font-bold mb-2 text-2xl">Desde</label>
                                    <input 
                                    id="fromDate"
                                    name="fromDate"
                                    type="date"
                                    onChange={e => setFromDate(e.target.value)}
                                    value={fromDate}
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    />
              </div>
              <div className="flex flex-col justify-center">
                  <label htmlFor="toDate" className="block text-gray-700 font-bold mb-2 text-2xl">Hasta</label>
                                    <input 
                                    id="toDate"
                                    name="toDate"
                                    type="date"
                                    onChange={e => setToDate(e.target.value)}
                                    value={toDate}
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    />
              </div>
              </div>
            </div>
            <div className="flex justify-center">
              <button onClick={() => filterReset()} className="bg-yellow-500 text-black font-semibold px-4 rounded shadow-lg h-14 w-3/4 lg:w-48 mx-auto lg:mx-12 my-8 ">Borrar Filtros de Fecha</button>
            </div>
          </div>
            {/* FILTRO DE ESTADO */}
            <div className="font-style: italic w-full flex sm:flex-row flex-col lg:px-6 justify-evenly align-middle items-center">
                <button className={`border rounded-md h-12 m-2 w-4/5 ${sharedTicket === "shared" ? "bg-yellow-500 shadow-lg" : "bg-gray-300"}`} onClick={() => setSharedTicket("shared")}> Familiar </button>
                <button className={`border rounded-md h-12 m-2 w-4/5 ${sharedTicket === "personal" ? "bg-yellow-500 shadow-lg" : "bg-gray-300"}`} onClick={() => setSharedTicket("personal")}> Personal </button>
                <button className={`border rounded-md h-12 m-2 w-4/5 ${sharedTicket === "negocio" ? "bg-yellow-500 shadow-lg" : "bg-gray-300"}`} onClick={() => setSharedTicket("negocio")}> Negocio </button>
            </div>
            {/* RESULTADOS */}
            <div className="flex sm:flex-row flex-col min-h-80">
                <div className="lg:w-2/4 w-full min-h-80">
                  <h1 className="font-bold text-2xl text-center text-yellow-500">Gastos</h1>
                  <div className="">
                    {filteredExpenses.map((filteredTicket) => (
                      <TicketCard key={filteredTicket.id} data={filteredTicket} />
                      ))}
                  </div>
                </div>
                <div className="lg:w-2/4 w-full ">
                  <h1 className="font-bold text-2xl text-center text-yellow-500">Ingresos</h1>
                  {filteredIncome.map((filteredTicket) => (
                      <TicketCard key={filteredTicket.id} data={filteredTicket} />
                    ))}
                </div>
            </div>
            {/* FINANCE CARD */}
            {/* <div>
              <FinanceCard
                totalExpenses={totalExpenses}
                totalIncome={totalIncome}
                balance={balance}
              />
            </div> */}
      </div>
    )
  }
  
  export default Dashboard
