import { useState, useEffect } from "react"
import { onSnapshot } from "firebase/firestore"
import { addData, getCollectionRef, getDataById, updateData } from "../../Services/CRUD"
import { useNavigate } from "react-router-dom"
import { useParams } from "react-router-dom"

const TicketPage = ({editMode}) => {
    const navigate = useNavigate()
    const [propertyList, setPropertyList] = useState([])
    const [formData, setFormData] = useState({
        timestamp: new Date().toISOString()
    })
    let {id} = useParams()

    const handleSubmit = async (e) => {
        e.preventDefault()
        if(!editMode){
            await addData("tickets", {
                property: formData.property,
                subindex: formData.subindex,
                amount: formData.amount,
                type: formData.type,
                concept: formData.concept,
                timestamp: formData.timestamp,
                date: formData.date,
            })
            navigate('/')
        }
        if (editMode){
            await updateData("tickets", id, {
                property: formData.property,
                subindex: formData.subindex,
                amount: formData.amount,
                type: formData.type,
                concept: formData.concept,
                timestamp: formData.timestamp,
                date: formData.date,
            })
            navigate('/')
        }
    }

    const fetchData = async () => {
       const response =  await getDataById("tickets", id)
        setFormData({
            property: response.property,
            subindex: response.subindex,
            amount: response.amount,
            type: response.type,
            concept: response.concept,
            timestamp: response.timestamp,
            date: response.date,
            })
    }

    useEffect(() => {
        if(editMode) fetchData()
    }, [])

    const handleChange = (e) => {
        const value = e.target.value
        const name = e.target.name

        setFormData((prevState) => ({
            ...prevState,
            [name]: value
        })
        )
    }
    return (
        <div className="flex flex-col">
            <h1 className="text-black text-center">{editMode ? 'Actualiza tu movimiento' : 'Crear un movimiento'}</h1>
            <div className="flex flex-col">
                <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 m-4 w-3/4">
                    <section className="flex flex-col flex-wrap w-3/4 m-10">
                            {formData.property?
                            <>
                            
                            <label htmlFor="subindex" className="block text-gray-700 text-sm font-bold mb-2">Numero Interior</label>
                            <select 
                                name="subindex"
                                value={formData.subindex}
                                onChange={handleChange}
                                required={true}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                               <>
                               <option value={'disable-value'} > -- Selecciona una opcion -- </option>
                                {propertyList.filter(expense => expense.property === formData.property)
                                .map((filteredBuilding) => (
                                    <>
                                        <option key={filteredBuilding.id} > {filteredBuilding.subindex} </option>
                                        <option key={formData.id} > {filteredBuilding.subindex1} </option>
                                        <option key={formData.id} > {filteredBuilding.subindex2} </option>
                                        <option key={formData.id} > {filteredBuilding.subindex3} </option>
                                        <option key={formData.id} > {filteredBuilding.subindex4} </option>
                                    </>
                                    ))
                                }
                               </>
                                    
                            </select>
                            </>
                               :""}
                            
                            <label htmlFor="type"className="block text-gray-700 text-sm font-bold mb-2">Tipo de Movimiento</label>
                            <select 
                                name="type"
                                value={formData.type}
                                onChange={handleChange}
                                required={true}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                                <option value={'disable-value'} disabled> -- Selecciona una opcion -- </option>
                                <option value="expense">Gasto / Egreso</option>
                                <option value="income" >Ingreso</option>
                            </select>
                            <label htmlFor="amount" className="block text-gray-700 text-sm font-bold mb-2">Monto</label>
                            <input 
                            id="amount"
                            name="amount"
                            type="number"
                            onChange={handleChange}
                            required={true}
                            value={formData.amount}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                            <label htmlFor="concept" className="block text-gray-700 text-sm font-bold mb-2">Concepto</label>
                            <input 
                            id="concept"
                            name="concept"
                            type="text"
                            onChange={handleChange}
                            required={true}
                            value={formData.concept}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                            <label htmlFor="date" className="block text-gray-700 text-sm font-bold mb-2">Fecha</label>
                            <input 
                            id="date"
                            name="date"
                            type="date"
                            onChange={handleChange}
                            required={true}
                            value={formData.date}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                        <input value="Enviar" type="submit" className="bg-yellow-400 hover:bg-yellow-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline cursor-pointer mt-9" />
                    </section>
                </form>
            </div>
        </div>
    )
    
}
export default TicketPage
