import { useState, useEffect } from "react"
import { onSnapshot } from "firebase/firestore"
import { addData, getCollectionRef, getDataById, updateData } from "../../Services/CRUD"
import { useNavigate } from "react-router-dom"
import { useParams } from "react-router-dom"

const TicketPage = ({editMode}) => {
    const [propertyList, setPropertyList] = useState([])
    const navigate = useNavigate()

    const [formData, setFormData] = useState({
        timestamp: new Date().toISOString()
    })
    let {id} = useParams()
    const docId = `http://localhost:3000/ticket/${id}`

    const handleSubmit = async (e) => {
        e.preventDefault()
        if(!editMode){
            await addData("tickets", {
                property: formData.property,
                subindex: formData.subindex,
                amount: formData.amount,
                type: formData.type,
                concept: formData.concept,
                timestamp: formData.timestamp,
                date: formData.date,
            })
            navigate('/')
        }
        if (editMode){
            await updateData("tickets", docId, {
                property: formData.property,
                subindex: formData.subindex,
                amount: formData.amount,
                type: formData.type,
                concept: formData.concept,
                timestamp: formData.timestamp,
                date: formData.date,
            })
        }
    }

    useEffect(() => {
        const unSubscribe = onSnapshot(getCollectionRef("properties"), (data) => {
          setPropertyList(
            data.docs.map((doc) => {
              return {...doc.data(), id: doc.id}
            })
          )
        })
        return () => {
          unSubscribe()
        }
       }, [])

    const handleChange = (e) => {
        const value = e.target.value
        const name = e.target.name

        setFormData((prevState) => ({
            ...prevState,
            [name]: value
        })
        )
    }

    return (
        <div className="flex flex-col">
            <h1 className="text-black text-center">{editMode ? 'Update your Ticket' : 'Create a Ticket'}</h1>
            <div className="flex flex-col">
                <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 m-4 w-3/4">
                    <section className="flex flex-col flex-wrap w-3/4 m-10">
                            <label htmlFor="property" className="block text-gray-700 text-sm font-bold mb-2">Property</label>
                            <select 
                                name="property"
                                value={formData.property}
                                onChange={handleChange}
                                required={true}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                                    <>
                                    <option disabled value={'disable-value'}> -- select an option -- </option>
                                    {propertyList?.map((uniqueBuilding, buildingIndex) => (
                                    <option key={buildingIndex}>{uniqueBuilding.property}</option>
                                        ))}
                                    </>

                            </select>


                            <label htmlFor="subindex" className="block text-gray-700 text-sm font-bold mb-2">Property Subindex</label>
                            <select 
                                name="subindex"
                                value={formData.subindex}
                                onChange={handleChange}
                                required={true}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                               {formData.property?
                               <>
                               <option value={'disable-value'} > -- select an option -- </option>
                                {propertyList.filter(expense => expense.property === formData.property)
                                .map((filteredBuilding, _index) => (
                                 <option key={_index} > {filteredBuilding.subindex} </option>
                                    ))
                                }
                               </>
                               :""}

                            </select>

                            <label htmlFor="type"className="block text-gray-700 text-sm font-bold mb-2">Type</label>
                            <select 
                                name="type"
                                value={formData.type}
                                onChange={handleChange}
                                required={true}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                                <option value={'disable-value'} disabled> -- select an option -- </option>
                                <option value="expense">Expense</option>
                                <option value="income" >Income</option>
                            </select>
                            <label htmlFor="amount" className="block text-gray-700 text-sm font-bold mb-2">Amount</label>
                            <input 
                            id="amount"
                            name="amount"
                            type="number"
                            onChange={handleChange}
                            required={true}
                            value={formData.amount}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                            <label htmlFor="concept" className="block text-gray-700 text-sm font-bold mb-2">Concept</label>
                            <input 
                            id="concept"
                            name="concept"
                            type="text"
                            onChange={handleChange}
                            required={true}
                            value={formData.concept}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                            <label htmlFor="date" className="block text-gray-700 text-sm font-bold mb-2">Date</label>
                            <input 
                            id="date"
                            name="date"
                            type="date"
                            onChange={handleChange}
                            required={true}
                            value={formData.date}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                        <input type="submit" className="bg-yellow-400 hover:bg-yellow-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline cursor-pointer mt-9" />
                    </section>
                </form>
            </div>
        </div>
    )

}
export default TicketPage
