import { useEffect, useState } from "react"
import { onSnapshot } from "firebase/firestore"
import { getCollectionRef } from "../../Services/CRUD"
import TicketCard from "../../Components/TicketCard"
// import Chart from "../Chart"

const Dashboard = () => {
  const [listaTickets, setListaTickets] = useState([])
  const [propertyList, setPropertyList] = useState([])
  const [fromDate, setFromDate] = useState("")
  const [toDate, setToDate] = useState("")
  // const [propertySelected, setPropertySelected] = useState("")

  useEffect(() => {
    const unSubscribe = onSnapshot((getCollectionRef("tickets")), (data) => {
      setListaTickets(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
  }, [])

  useEffect(() => {
    const unSubscribe = onSnapshot(getCollectionRef("properties"), (data) => {
      setPropertyList(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
   }, [])

 const uniqueBuildingsWithTickets = [
   ...new Set(listaTickets?.map((({property}) => property) ))
  ]  

  const filterReset = () => {
      setFromDate("")
      setToDate("")
    }

    console.log('lista tickets ' + listaTickets)

  return (
    <div>
          <div className="flex flex-row justify-evenly">
            <h2 className="text-center text-2xl">Filtro por fechas</h2>
            <div className="flex justify-around">
            <div className="flex flex-col justify-center">
                <label htmlFor="fromDate" className="block text-gray-700 text-sm font-bold mb-2 text-2xl">Desde</label>
                                  <input 
                                  id="fromDate"
                                  name="fromDate"
                                  type="date"
                                  onChange={e => setFromDate(e.target.value)}
                                  value={fromDate}
                                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                  />
            </div>
            <div>
                <label htmlFor="toDate" className="block text-gray-700 text-sm font-bold mb-2 text-2xl">Hasta</label>
                                  <input 
                                  id="toDate"
                                  name="toDate"
                                  type="date"
                                  onChange={e => setToDate(e.target.value)}
                                  value={toDate}
                                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                  />
            </div>
            </div>
            <button onClick={() => filterReset()}>Quitar Filtros</button>
            {/* <div className="flex justify-center">
              <h2 className="text-center text-2xl">Filtro por Propiedad</h2>
              <select 
                                      name="property"
                                      value={propertySelected}
                                      onChange={e => setPropertySelected(e.target.value)}
                                      defaultValue={'disable-value'}
                                      className="shadow appearance-none border rounded w-1/3 py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                      >
                                          <>
                                          <option value={'disable-value'}> -- selecciona una opcion -- </option>
                                          {propertyList?.map((uniqueBuilding) => (
                                          <option key={uniqueBuilding.id}>{uniqueBuilding.property}</option>
                                              ))}
                                          </>

              </select>
              </div>  */}
            </div> 

      <div className="font-style: italic w-full flex flex-wrap">
          <div className="w-2/4">
                <h1 className="font-bold text-2xl text-center">Gastos</h1>
                <div className="">
                  {uniqueBuildingsWithTickets?.map((uniqueBuilding, buildingIndex) => (
                    <div key={buildingIndex}>
                      <h3 className="font-bold text-xl">{uniqueBuilding}</h3>
                        {listaTickets.filter(data => data.property === uniqueBuilding && data.type === 'expense' && 
                        ( fromDate && toDate ?
                        (data.date >= fromDate && data.date <= toDate) : data.date))
                        .map((filteredBuilding, _index) => (
                          <TicketCard
                          id={_index}
                          data={filteredBuilding}
                          />
                          ))
                        }
                    </div>
                  ))}
                </div>
          </div>
            <div className="w-2/4">
              <h1 className="font-bold text-2xl text-center">Ingresos</h1>
              <div className="w-full">
                  {uniqueBuildingsWithTickets?.map((uniqueBuilding, buildingIndex) => (
                    <div key={buildingIndex}>
                      <h3 className="font-bold text-xl">{uniqueBuilding}</h3>
                        {listaTickets.filter(data => data.property === uniqueBuilding && data.type === 'income' && 
                        ( fromDate && toDate ?
                        (data.date >= fromDate && data.date <= toDate) : data.date) )
                        .map((filteredBuilding, _index) => (
                          <TicketCard
                          id={_index}
                          data={filteredBuilding}
                          />
                          ))
                        }
                    </div>
                  ))}
                </div>
            </div>
      </div>
      <div>
        <p>Resumen</p>
        {fromDate && toDate ? <p>Desde {fromDate} hasta {toDate}</p> : "" }
        <div>
        <p>Ingreso</p>
        <p>Egreso</p>
        <p>Comision por Administracion</p>
        </div>

      </div>
    </div>
    )
  }

  export default Dashboard
