import { useState, useEffect } from "react"
import { onSnapshot } from "firebase/firestore"
import { addData, getCollectionRef } from "../../Services/CRUD"
import { useNavigate } from "react-router-dom"

const PropertyPage = () => {
    const [propertyList, setPropertyList] = useState([])
    const navigate = useNavigate()

    const handleSubmit = async (e) => {
        e.preventDefault()
        await addData("properties", {
            property: formData.property,
            subindex: formData.subindex,
            timestamp: formData.timestamp,
        })
        navigate('/')
    }

    useEffect(() => {
        const unSubscribe = onSnapshot(getCollectionRef("properties"), (data) => {
          setPropertyList(
            data.docs.map((doc) => {
              return {...doc.data(), id: doc.id}
            })
          )
        })
        return () => {
          unSubscribe()
        }
       }, [])


    const [formData, setFormData] = useState({})
    
    const editMode = false

    const handleChange = (e) => {
        const value = e.target.value
        const name = e.target.name

        setFormData((prevState) => ({
            ...prevState,
            [name]: value
        })
        )
    }
    console.log(formData)
    return (
        <div className="flex flex-col">
            <h1 className="text-black text-center">{editMode ? 'Update your Property' : 'Create a Property'}</h1>
            <div className="flex flex-col">
                <form onSubmit={handleSubmit} className="flex flex-col">
                    <section className="flex flex-col flex-wrap lg:flex-row w-3/4 m-10">
                            <label htmlFor="property">Property</label>
                            <input 
                            id="property"
                            name="property"
                            type="text"
                            onChange={handleChange}
                            required={true}
                            value={formData.property}
                            />
                            <label htmlFor="streetName">Street Name</label>
                            <input 
                            id="streetName"
                            name="streetName"
                            type="text"
                            onChange={handleChange}
                            required={true}
                            value={formData.streetName}
                            />
                            <label htmlFor="streetNumber">Street Number</label>
                            <input 
                            id="streetNumber"
                            name="streetNumber"
                            type="text"
                            onChange={handleChange}
                            required={true}
                            value={formData.streetNumber}
                            />
                        <input type="submit" />
                    </section>
                </form>
            </div>
        </div>
    )
    
}
export default PropertyPage
