import { useEffect, useState } from "react"
import { onSnapshot } from "firebase/firestore"
import { getCollectionRef } from "../../Services/CRUD"
import { useNavigate } from "react-router-dom"


const SideBar = () => {
  const [listaDeptos, setListaDeptos] = useState([])
  const navigate = useNavigate()
  // date = new Date().toLocaleDateString()

  useEffect(() => {
    const unSubscribe = onSnapshot(getCollectionRef("expenses"), (data) => {
      setListaDeptos(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
   }, [])

  return (<aside className="w-1/5">
        <button onClick={() => navigate('/')} className="bg-white hover:bg-gray-100 text-gray-800 font-semibold py-2 px-4 border border-gray-400 rounded shadow ml-12 w-3/4 m-4"> Dashboard </button>
        <div>
        {listaDeptos.map((deptos) => {
            console.log(deptos.property)
            return (
              <>
              <button className="bg-white hover:bg-gray-100 text-gray-800 font-semibold py-2 px-4 border border-gray-400 rounded shadow ml-12 w-3/4 m-4">{deptos.property}</button>
              </>
            )
          })
        }
        </div>
      </aside>)
    }

export default SideBar
