import { useEffect, useState } from "react"
import { onSnapshot } from "firebase/firestore"
import { getCollectionRef } from "../../Services/CRUD"
import { useNavigate } from "react-router-dom"
import { Link } from "react-router-dom"


const Aside = () => {
  const [listaDeptos, setListaDeptos] = useState([])
  const navigate = useNavigate()

  useEffect(() => {
    const unSubscribe = onSnapshot(getCollectionRef("properties"), (data) => {
      setListaDeptos(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
   }, [])

   return <aside className="w-full lg:w-1/5 pt-2 lg:bg-pink-300 mx-auto">
        <button onClick={() => navigate('/')} className="bg-white hover:bg-pink-400 text-gray-800 font-semibold py-2 px-2 border border-gray-400 rounded shadow ml-8 w-4/5"> Inicio </button>
        <div>
        <h1 className="font-bold text-lg text-center my-6">Propiedades</h1>
        {listaDeptos?.map((uniqueBuilding, buildingIndex) => (
          <div key={buildingIndex} className="my-8 mx-auto">
            <Link to={`/property/${uniqueBuilding.id}` } className="mx-auto">
              <button className="bg-white hover:bg-pink-400 text-gray-800 font-semibold py-2 my-0 border border-gray-400 rounded shadow mx-8 w-4/5" >{uniqueBuilding.property}</button>
            </Link>
          </div>
              ))}
            </div>
      </aside>
    }

export default Aside
