import { useState } from "react"

const TicketPage = () => {
    const [listaDeptos, setListaDeptos] = useState([])

    const uniqueBuildings = [
        ...new Set(listaDeptos?.map(({property}) => property))
       ]

    const [formData, setFormData] = useState({
        timestamp: new Date().toISOString()
    })

    const editMode = false

    const handleSubmit = () => {
        console.log('submitted')
    }
    const handleChange = (e) => {
        const value = e.target.value
        const name = e.target.name

        setFormData((prevState) => ({
            ...prevState,
            [name]: value
        })
        )
    }
    console.log(formData)
    return (
        <div>
            <h1>{editMode ? 'Update your Ticket' : 'Create a Ticket'}</h1>
            <div>
                <form onSubmit={handleSubmit}>
                    <section>
                            <label htmlFor="property">Property</label>
                            <select 
                                name="property"
                                value={formData.property}
                                onChange={handleChange}
                                >

                                {uniqueBuildings?.map((uniqueBuilding, buildingIndex) => (
                <div key={buildingIndex}>
                  <h3 className="font-bold text-xl">{uniqueBuilding}</h3>
                    {listaDeptos.filter(expense => expense.property === uniqueBuilding && expense.type === 'expense')
                    .map((filteredBuilding, _index) => (
                      <TicketCard
                      id={_index}
                      color={filteredBuilding.color}
                      expense={filteredBuilding}
                      />
                      ))
                    }
                </div>
              ))}
                            </select>
                            <label htmlFor="type">Type</label>
                            <select name="type">
                                <option value="expense">Expense</option>
                                <option value="income" selected>Income</option>
                            </select>
                            <label htmlFor="amount">Amount</label>
                            <input 
                            id="amount"
                            name="amount"
                            type="number"
                            onChange={handleChange}
                            required={true}
                            value={formData.amount}
                            />
                            <label htmlFor="concept">Concept</label>
                            <input 
                            id="concept"
                            name="concept"
                            type="text"
                            onChange={handleChange}
                            required={true}
                            value={formData.concept}
                            />

                    </section>
                </form>
            </div>
        </div>
    )
}

export default TicketPage
