import { useEffect, useState } from "react"
import SignOut from "../SignOutEmail"
import { onSnapshot } from "firebase/firestore"
import { getCollectionRef } from "../../Services/CRUD"

const HomePage = () => {
  const [listaDeptos, setListaDeptos] = useState([])
  // date = new Date().toLocaleDateString()

  useEffect(() => {
    const unSubscribe = onSnapshot(getCollectionRef("SanPedro"), (data) => {
      setListaDeptos(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
   }, [])

  return (<div>

    <div>
    <h1 className="logro">Lo Lograste!</h1>
    <SignOut/>
    {listaDeptos.map((deptos) => {
        console.log(deptos)
        return (
          <>
          <h2>{deptos.id}</h2>
          <p>Ingreso</p>
          <p>{deptos.Mantenimiento} </p>
          <p>Gasto</p>
          <p>{deptos.Gasto}</p>
          </>
        )
      })
    }
    </div>
  </div>)
}

export default HomePage
