import { useState, useEffect } from "react"
import { onSnapshot } from "firebase/firestore"
import { addData } from "../../Services/CRUD"
import { useNavigate } from "react-router-dom"
import { getCollectionRef } from "../../Services/CRUD"

const PropertyPage = () => {
    const [formPropertyData, setFormPropertyData] = useState({
        timestamp: new Date().toISOString()
    })

    const navigate = useNavigate()

    const handleSubmit = async (e) => {
        e.preventDefault()
        await addData("properties", {
            property: formPropertyData.property,
            streetName: formPropertyData.streetName,
            streetNumber: formPropertyData.streetNumber,
            subindex: formPropertyData.subindex,
            timestamp: formPropertyData.timestamp,
        })
        navigate('/')
    }

    useEffect(() => {
        const unSubscribe = onSnapshot(getCollectionRef("tickets"), (data) => {
          set(
            data.docs.map((doc) => {
              return {...doc.data(), id: doc.id}
            })
          )
        })
        return () => {
          unSubscribe()
        }
       }, [])
    
    // console.log(formPropertyData)
    const editMode = false

    const handleChange = (e) => {
        const value = e.target.value
        const name = e.target.name

        setFormPropertyData((prevState) => ({
            ...prevState,
            [name]: value
        })
        )
    }

    return (
        <div className="flex flex-col">
            <h1 className="text-black text-center">{editMode ? 'Update your Property' : 'Create a Property'}</h1>
            <div className="flex flex-col">
                <form onSubmit={handleSubmit} className="flex flex-col">
                    <section className="flex flex-col w-full m-10">
                            <div className="w-4/5">
                                <label htmlFor="property">Property</label>
                                <input 
                                className="bg-yellow-100"
                                id="property"
                                name="property"
                                type="text"
                                onChange={handleChange}
                                required={true}
                                value={formPropertyData.property}
                                />
                            </div>
                            <div>
                                <label htmlFor="streetName">Street Name</label>
                                <input 
                                className="bg-yellow-100"
                                id="streetName"
                                name="streetName"
                                type="text"
                                onChange={handleChange}
                                required={true}
                                value={formPropertyData.streetName}
                                />
                            </div>
                            <div>
                                <label htmlFor="streetNumber">Street Number</label>
                                <input 
                                className="bg-yellow-100"
                                id="streetNumber"
                                name="streetNumber"
                                type="number"
                                onChange={handleChange}
                                required={true}
                                value={formPropertyData.streetNumber}
                                />
                            </div>
                            <div>
                            <label htmlFor="subindex">Interior Id</label>
                                    <input 
                                    id="subindex"
                                    name="subindex"
                                    type="text"
                                    onChange={handleChange}
                                    required={true}
                                    value={formPropertyData.subindex}
                                    />
                                     <button> + Subindice </button>
                             </div>
                
                        <input type="submit" />
                    </section>
                </form>
            </div>
        </div>
    )
    
}
export default PropertyPage
