import { useEffect, useState } from "react"
import { onSnapshot } from "firebase/firestore"
import { getCollectionRef } from "../../Services/CRUD"
import TicketCard from "../../Components/TicketCard"
import Chart from "../Chart"

const Dashboard = () => {
  const [listaTickets, setListaTickets] = useState([])
  const [buildingName, setBuildingName] = useState("")

  useEffect(() => {
    const unSubscribe = onSnapshot(getCollectionRef("tickets"), (data) => {
      setListaTickets(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
  }, [])

 const uniqueBuildings = [
   ...new Set(listaTickets?.map(({property}) => property))
  ]

  const handleChange = (e) => {
    const value = e.target.value

    setBuildingName(() => ({
        propertyName: value
    })
    )
}


  console.log('lista Tickets',listaTickets)

  const chartIncomeBuilding = listaTickets.filter(function(ticket){
    return (ticket.property === 'San Cosme' && ticket.type === 'income')
  })

  const amountIncomeArr = chartIncomeBuilding.map((property) => {
    return property.amount
  }) 

  const datesIncomeArr = chartIncomeBuilding.map((property) => {
    return property.date
  })  

  const chartExpenseBuilding = listaTickets.filter(function(ticket){
    return (ticket.property === 'San Cosme' && ticket.type === 'expense')
  })

  const amountExpenseArr = chartExpenseBuilding.map((property) => {
    return property.amount
  }) 

  const datesExpenseArr = chartExpenseBuilding.map((property) => {
    return property.date
  })  




  console.log('this is buildingName', buildingName)
  // console.log('this is chartBuilding', chartBuilding)
  console.log('this is amount arr',amountIncomeArr)
  console.log('this is dates arr',datesIncomeArr)

  return (
    <div>


      <select 
      name="buildingName" 
      value={buildingName}
      onChange={handleChange}
      required={true}
      defaultValue={'disable-value'}
      className="shadow appearance-none border rounded w-2/4 py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
      >
        <>
        <>
        <option disabled value={'disable-value'}> -- select an option -- </option>
        </>
        {listaTickets?.map((uniqueBuilding) => (
        <option key={uniqueBuilding.id}>{uniqueBuilding.property}</option>
        ))}
        </>
      </select>

      <Chart
      incomeDates={datesIncomeArr}
      incomeAmount={amountIncomeArr}
      expenseDates={datesExpenseArr}
      expenseAmount={amountExpenseArr}
      buildingName={buildingName}
      />


   <div className="font-style: italic w-full flex flex-wrap">
      <div className="w-2/4">
            <h1 className="font-bold text-2xl text-center">Gastos</h1>
            <div className="">
              {uniqueBuildings?.map((uniqueBuilding, buildingIndex) => (
                <div key={buildingIndex}>
                  <h3 className="font-bold text-xl">{uniqueBuilding}</h3>
                    {listaTickets.filter(data => data.property === uniqueBuilding && data.type === 'expense')
                    .map((filteredBuilding, _index) => (
                      <TicketCard
                      id={_index}
                      data={filteredBuilding}
                      />
                      ))
                    }
                </div>
              ))}
            </div>
      </div>
        <div className="w-2/4">
          <h1 className="font-bold text-2xl text-center">Ingresos</h1>
          <div className="w-full">
              {uniqueBuildings?.map((uniqueBuilding, buildingIndex) => (
                <div key={buildingIndex}>
                  <h3 className="font-bold text-xl">{uniqueBuilding}</h3>
                    {listaTickets.filter(data => data.property === uniqueBuilding && data.type === 'income')
                    .map((filteredBuilding, _index) => (
                      <TicketCard
                      id={_index}
                      color={filteredBuilding.color}
                      data={filteredBuilding}
                      />
                      ))
                    }
                </div>
              ))}
            </div>
        </div>
    </div>
              </div>
    )
  }

  export default Dashboard
