import { useEffect, useState } from "react"
import { onSnapshot } from "firebase/firestore"
import { getCollectionRef } from "../../Services/CRUD"
import TicketCard from "../../Components/TicketCard"
import { From } from "../../Components/Filters"
import { To } from "../../Components/Filters"
// import Chart from "../Chart"

const Dashboard = () => {
  const [listaTickets, setListaTickets] = useState([])
  const [propertyList, setPropertyList] = useState([])
  const [fromDate, setFromDate] = ("")
  const [toDate, setToDate] = ("")
  // const [buildingName, setBuildingName] = useState("")

  useEffect(() => {
    const unSubscribe = onSnapshot(getCollectionRef("tickets"), (data) => {
      setListaTickets(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
  }, [])

  useEffect(() => {
    const unSubscribe = onSnapshot(getCollectionRef("properties"), (data) => {
      setPropertyList(
        data.docs.map((doc) => {
          return {...doc.data(), id: doc.id}
        })
      )
    })
    return () => {
      unSubscribe()
    }
   }, [])

 const uniqueBuildingsWithTickets = [
   ...new Set(listaTickets?.map(({property}) => property ))
  ]

  // console.log('this is unique buildings with tickets' + uniqueBuildingsWithTickets)

  // <select
  //     name="buildingName" 
