  // console.log('this is chartBuilding', chartBuilding)
  // console.log('this is amount arr',amountIncomeArr)
  // console.log('this is dates arr',datesIncomeArr)

  return (
    <div>
    <div>
      <h2 className="text-center">Filtro por fechas</h2>
      <From
      toDate = {toDate}
      />
      <To/>
      <div className="flex justify-center">
        <h2 className="text-center">Filtro por Propiedad</h2>
        <select 
                                name="property"
                                // value={formData.property}
                                // onChange={handleChange}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-1/3 py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                                    <>
                                    <option disabled value={'disable-value'}> -- selecciona una opcion -- </option>
                                    {propertyList?.map((uniqueBuilding) => (
                                    <option key={uniqueBuilding.id}>{uniqueBuilding.property}</option>
                                        ))}
                                    </>

                            </select>
      </div>
    </div>




      {/* <Chart
      incomeDates={datesIncomeArr}
      incomeAmount={amountIncomeArr}
      expenseDates={datesExpenseArr}
      expenseAmount={amountExpenseArr}
      buildingName={buildingName}
      /> */}


   <div className="font-style: italic w-full flex flex-wrap">
      <div className="w-2/4">
            <h1 className="font-bold text-2xl text-center">Gastos</h1>
            <div className="">
              {uniqueBuildingsWithTickets?.map((uniqueBuilding, buildingIndex) => (
                <div key={buildingIndex}>
                  <h3 className="font-bold text-xl">{uniqueBuilding}</h3>
                    {listaTickets.filter(data => data.property === uniqueBuilding && data.type === 'expense')
                    .map((filteredBuilding, _index) => (
                      <TicketCard
                      id={_index}
                      data={filteredBuilding}
                      />
                      ))
                    }
                </div>
              ))}
            </div>
      </div>
        <div className="w-2/4">
          <h1 className="font-bold text-2xl text-center">Ingresos</h1>
          <div className="w-full">
              {uniqueBuildingsWithTickets?.map((uniqueBuilding, buildingIndex) => (
                <div key={buildingIndex}>
                  <h3 className="font-bold text-xl">{uniqueBuilding}</h3>
                    {listaTickets.filter(data => data.property === uniqueBuilding && data.type === 'income')
                    .map((filteredBuilding, _index) => (
                      <TicketCard
                      id={_index}
                      data={filteredBuilding}
                      />
                      ))
                    }
                </div>
              ))}
            </div>
        </div>
    </div>
