import React from 'react';
import { Document, Page, Text} from '@react-pdf/renderer';


const TicketPDF = ({clientDetails, formData, objetoEnviado}) => {
  return (
    <Document>
        <Page size="A4" paddingHorizontal="20px" paddingVertical="15px">
            <Text style={{textAlign: 'right', fontSize: '22px' }}>{formData.date}</Text>
            <Text style={{textAlign: 'center',fontSize: '22px' }}>{`Remision: ${formData.ticketNumber}`}</Text>
            <div>
                <Text style={{fontSize: '18px' }}>{`Nombre del cliente: ${formData.clientName}`}</Text>
                <Text style={{fontSize: '18px' }}>{`Direccion de envio: ${clientDetails.direction}`}</Text>
                <Text style={{fontSize: '18px' }}>{`Telefono: ${clientDetails.phone}`}</Text>
            </div>
            <Text style={{fontSize: '22px' }}>Detalle de productos:</Text>
            {objetoEnviado.map((objeto, index) => (
                <div key={index}>
                {/* Muestra los detalles de cada objeto del array */}
                <Text>{`Producto ${index + 1}`}</Text>
                {Object.entries(objeto).map(([key, value], i) => {
                    let displayedKey = '';
                    switch (key) {
                    case 'cantidadProducto':
                        displayedKey = 'Cantidad';
                        break;
                    case 'nombreProducto':
                        displayedKey = 'Nombre';
                        break;
                    case 'precioProducto':
                        displayedKey = 'Precio';
                        break;
                    default:
                        displayedKey = key;
                        break;
                    }
                    return <Text key={i}>{`${displayedKey}: ${value}`}</Text>;
                })}
                </div>
            ))}
            <Text>{`Remision: ${formData.amount}`}</Text>
        </Page>
    </Document>
  )
}

export default TicketPDF
