import { useState, useEffect, useContext} from "react"
import { onSnapshot } from "firebase/firestore"
import { getCollectionRef } from "../../Services/CRUD"
import { userContext } from "../../context/userProvider"
import { addData, getData, getDataById, updateData } from "../../Services/CRUD"
import { useNavigate } from "react-router-dom"
import { useParams } from "react-router-dom"

const getNextConsecutiveNumber = async () => {
    const currentYear = new Date().getFullYear();
  
    const tickets = await getData("tickets");
  
    const currentYearTickets = tickets.filter(
      (ticket) =>
        ticket.timestamp && new Date(ticket.timestamp).getFullYear() === currentYear
    );
  
    let maxNumber = 0;
    if (currentYearTickets.length > 0) {
      maxNumber = currentYearTickets.reduce((max, ticket) => {
        if (ticket.ticketNumber) {
          const ticketNumber = parseInt(ticket.ticketNumber.substring(4), 10);
          return ticketNumber > max ? ticketNumber : max;
        }
        return max;
      }, 0);
    }
  
    const nextNumber = maxNumber + 1;
    const formattedNumber = nextNumber.toString().padStart(3, "0");
    const ticketID = `${currentYear}${formattedNumber}`;
  
    return ticketID;
  };
  
  



const TicketPage = ({editMode}) => {
    const user = useContext(userContext)
    const navigate = useNavigate()
    const [formData, setFormData] = useState({
        timestamp: new Date().toISOString(),
        ticketNumber: '', // Nuevo campo para el número consecutivo
        date: new Date().toISOString().split('T')[0], // Establecer la fecha actual
    })
    const [cantidadProducto, setCantidadProducto] = useState('');
    const [nombreProducto, setNombreProducto] = useState('');
    const [precioProducto, setPrecioProducto] = useState('');
    const [objetoEnviado, setObjetoEnviado] = useState([]);
    const [products, setProducts] = useState([]);
    let {id} = useParams()
    
    // Obtener el próximo número consecutivo al montar el componente
        useEffect(() => {
            const fetchNextTicketID = async () => {
                const nextID = await getNextConsecutiveNumber();
                setFormData((prevState) => ({ ...prevState, ticketNumber: nextID }));
              };
              

            fetchNextTicketID();
        }, []);

        const handleSubmit = async (e) => {
            e.preventDefault();
            console.log('formData.ticketNumber ' + formData.ticketNumber); // Verifica el valor de ticketNumber
          
            if (!editMode) {
                await addData("tickets", {
                  ticketNumber: formData.ticketNumber,
                  clientName: formData.clientName,
                  amount: formData.amount,
                  type: formData.type,
                  category: formData.category,
                  timestamp: formData.timestamp,
                  date: formData.date, // Establecer la fecha actual al enviar el formulario
                  uid: user.uid,
                });
                navigate("/");
              }
        if (editMode){
            await updateData("tickets", id, {
                ticketNumber: formData.ticketNumber,
                clientName: formData.clientName,
                amount: formData.amount,
                type: formData.type,
                category: formData.category,
                timestamp: formData.timestamp,
                date: formData.date,
                uid: user.uid,
            })
            navigate('/')
        }
    }

    const fetchData = async () => {
       const response =  await getDataById("tickets", id)
        setFormData({
            ticketNumber: response.ticketNumber,
            clientName: response.clientName,
            amount: response.amount,
            type: response.type,
            category: response.category,
            timestamp: response.timestamp,
            date: response.date,
            uid: user.uid
            })
    }

    useEffect(() => {
        if(editMode) fetchData()
    }, [])

    const handleChange = (e) => {
        const value = e.target.value;
        const name = e.target.name;
    
        setFormData((prevState) => ({
            ...prevState,
            [name]: value
        }));
    }

    // Agrega productos a un array

    useEffect(() => {
        const unsubscribe = onSnapshot(getCollectionRef('products'), (data) => {
          setProducts(
            data.docs.map((doc) => {
              return { ...doc.data(), id: doc.id };
            })
          );
        });
    
        return () => {
          unsubscribe();
        };
      }, []);

      useEffect(() => {
        const selectedProduct = products.find((product) => product.name === campo2);
        if (selectedProduct) {
          setPrecioProducto(selectedProduct.price.toString());
        } else {
          setPrecioProducto('');
        }
      }, [campo2, products]);

      const handleAddProduct = (e) => {
        e.preventDefault();
        const selectedProduct = products.find((product) => product.name === campo2);
        const listaDeProductos = {
          cantidadProducto,
          campo2,
          campo3: selectedProduct ? selectedProduct.price : '',
        };
        setObjetoEnviado([...objetoEnviado, listaDeProductos]);
        setCantidadProducto('');
        setCampo2('');
        setPrecioProducto('');
      };
      
      console.log(cantidadProducto, campo2, campo3 )
      console.log(objetoEnviado);

      

    return (
        <div className="flex flex-col justify-center align-middle items-center mx-auto ">
            <h1 className="text-black text-center flex justify-center align-middle items-center mx-auto text-2xl font-semibold mt-4">{editMode ? 'Actualiza tu movimiento' : 'Crear un movimiento'}</h1>
            <div className="flex flex-col mx-auto  w-full sm:w-4/5 items-center">
                <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 m-4 w-3/4">
                    <section className="flex flex-col flex-wrap w-3/4 mx-auto">
                      <label htmlFor="type"className="block text-gray-700 text-sm font-bold mb-2">Tipo de Movimiento</label>
                            <select 
                                name="type"
                                value={formData.type}
                                onChange={handleChange}
                                required={true}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                                <option value={'disable-value'} disabled> -- Selecciona una opcion -- </option>
                                <option value="expense">Remisiones</option>
                                <option value="income" >Depositos</option>
                            </select>
                            <label htmlFor="category"className="block text-gray-700 text-sm font-bold mb-2">Categoria</label>
                            <select 
                                name="category"
                                value={formData.category}
                                onChange={handleChange}
                                required={true}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                                <option value={'disable-value'} disabled> -- Selecciona una opcion -- </option>
                                <option value="nutricionales">Remision Nutricionales</option>
                                <option value="cosmeticos" >Remision Cosmeticos</option>
                                <option value="otros" >Otros</option>
                            </select>
                            <label htmlFor="clientName" className="block text-gray-700 text-sm font-bold mb-2">Nombre del Cliente</label>
                            <input 
                            id="clientName"
                            name="clientName"
                            type="text"
                            onChange={handleChange}
                            required={true}
                            value={formData.clientName}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                            <label htmlFor="amount" className="block text-gray-700 text-sm font-bold mb-2">Monto</label>
                            <input 
                            id="amount"
                            name="amount"
                            type="number"
                            onChange={handleChange}
                            required={true}
                            value={formData.amount}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                                <div>
                                    <label htmlFor="cantidad">Cantidad</label>
                                    <input
                                        type="text"
                                        value={cantidadProducto}
                                        onChange={(e) => setCantidadProducto(e.target.value)}
                                        placeholder="Cantidad"
                                        className="border w-1/4 m-2"
                                    />
                                </div>
                                <div>
                                    <label htmlFor="producto">Nombre del Producto</label>
                                    <select
                                    value={campo2}
                                    onChange={(e) => setCampo2(e.target.value)}
                                    placeholder="Campo 2"
                                    >
                                    <option value="">Seleccione un producto</option>
                                    {products.map((product, index) => (
                                        <option key={index} value={product.name}>
                                        {product.name}
                                        </option>
                                    ))}
                                    </select>
                                </div>
                                <div>
                                    <label htmlFor="precio">Precio</label>
                                    <input
                                    type="text"
                                    value={campo3}
                                    onChange={(e) => setPrecioProducto(e.target.value)}
                                    placeholder="Precio"
                                    className="border w-1/4 m-2"
                                    readOnly // Evita la modificación directa del campo 3 por el usuario
                                    />
                                </div>

                                <button onClick={handleAddProduct} className="w-1/4 border bg-yellow-500 rounded-lg text-sm">Agregar Productos</button>
                            <div>
                            {objetoEnviado.map((objeto, index) => {
  const { cantidadProducto, campo2, campo3 } = objeto;

  // Verifica si algún campo tiene datos ingresados
  const hasData = cantidadProducto || campo2 || campo3;

  // Si hay datos, muestra el objeto
  if (hasData) {
    return (
      <div key={index}>
        {cantidadProducto && <p>Cantidad: {cantidadProducto}</p>}
        {campo2 && <p>Nombre: {campo2}</p>}
        {campo3 && <p>Precio: {campo3}</p>}
      </div>
    );
  }

  // Si no hay datos, no muestra nada
  return null;
})}

                        </div>
                            
                        <input value="Enviar" type="submit" className="bg-yellow-400 hover:bg-yellow-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline cursor-pointer mt-9" />
                    </section>
                </form>
            </div>
        </div>
    )
    
}
export default TicketPage
