import { useState, useEffect, useContext } from "react";
import { userContext } from "../../context/userProvider";
import { addData, getData, getDataById, updateData } from "../../Services/CRUD";
import { useNavigate } from "react-router-dom";
import { useParams } from "react-router-dom";

const getNextConsecutiveNumber = async () => {
  const currentYear = new Date().getFullYear();

  const tickets = await getData("tickets");

  const currentYearTickets = tickets.filter(
    (ticket) =>
      ticket.timestamp &&
      new Date(ticket.timestamp).getFullYear() === currentYear
  );

  let maxNumber = 0;
  if (currentYearTickets.length > 0) {
    maxNumber = currentYearTickets.reduce((max, ticket) => {
      if (ticket.ticketNumber) {
        const ticketNumber = parseInt(ticket.ticketNumber.substring(4), 10);
        return ticketNumber > max ? ticketNumber : max;
      }
      return max;
    }, 0);
  }

  const nextNumber = maxNumber + 1;
  const formattedNumber = nextNumber.toString().padStart(3, "0");
  const ticketID = `${currentYear}${formattedNumber}`;

  return ticketID;
};

const TicketPage = ({ editMode }) => {
  const user = useContext(userContext);
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    timestamp: new Date().toISOString(),
    ticketNumber: "",
    amount: 0,
    products: [],
    selectedProduct: "",
    quantity: 1,
  });
  const [productsList, setProductsList] = useState([]);
  let { id } = useParams();

  useEffect(() => {
    const fetchProducts = async () => {
      const products = await getData("products");
      setProductsList(products);
    };

    fetchProducts();
  }, []);

  const handleAddProduct = () => {
    const selectedProduct = productsList.find(
      (product) => product.id === formData.selectedProduct
    );

    if (selectedProduct) {
      const newProduct = {
        id: selectedProduct.id,
        name: selectedProduct.name,
        price: selectedProduct.price,
        quantity: formData.quantity,
      };

      const updatedProducts = [...formData.products, newProduct];
      const totalAmount = calculateTotalAmount(updatedProducts);

      setFormData((prevData) => ({
        ...prevData,
        products: updatedProducts,
        amount: totalAmount,
      }));
    }
  };

  const calculateTotalAmount = (products) => {
    return products.reduce(
      (total, product) => total + product.price * product.quantity,
      0
    );
  };
    
    // Obtener el próximo número consecutivo al montar el componente
        useEffect(() => {
            const fetchNextTicketID = async () => {
                const nextID = await getNextConsecutiveNumber();
                setFormData((prevState) => ({ ...prevState, ticketNumber: nextID }));
              };
              

            fetchNextTicketID();
        }, []);

        const handleSubmit = async (e) => {
            e.preventDefault();
            console.log('formData.ticketNumber ' + formData.ticketNumber); // Verifica el valor de ticketNumber
          
            if (!editMode) {
                const ticketData = {
                  ticketNumber: formData.ticketNumber,
                  clientName: formData.clientName,
                  type: formData.type,
                  category: formData.category,
                  concept: formData.concept,
                  timestamp: formData.timestamp,
                  date: formData.date,
                  uid: user.uid,
                  amount: formData.amount, // Agregar el monto total al objeto de ticketData
                };
          
                await addData("tickets", ticketData);
          
                // Agregar productos al ticket en Firebase con detalles de cada producto
                const ticketID = await getNextConsecutiveNumber(); // Obtener el ID del ticket creado
          
                formData.products.forEach(async (product) => {
                  await addData(`tickets/${ticketID}/products`, {
                    productId: product.id,
                    quantity: product.quantity,
                    price: product.price,
                  });
                });
          
                navigate("/");
              }
        if (editMode){
            await updateData("tickets", id, {
                ticketNumber: formData.ticketNumber,
                clientName: formData.clientName,
                amount: formData.amount,
                type: formData.type,
                category: formData.category,
                concept: formData.concept,
                timestamp: formData.timestamp,
                date: formData.date,
                uid: user.uid,
            })
            navigate('/')
        }
    }

    const fetchData = async () => {
       const response =  await getDataById("tickets", id)
        setFormData({
            ticketNumber: response.ticketNumber,
            clientName: response.clientName,
            amount: response.amount,
            type: response.type,
            category: response.category,
            concept: response.concept,
            timestamp: response.timestamp,
            date: response.date,
            uid: user.uid
            })
    }

    useEffect(() => {
        if(editMode) fetchData()
    }, [])

    const handleChange = (e) => {
        const value = e.target.value;
        const name = e.target.name;
    
        setFormData((prevState) => ({
            ...prevState,
            [name]: value
        }));
    }
    return (
        <div className="flex flex-col justify-center align-middle items-center mx-auto ">
            <h1 className="text-black text-center flex justify-center align-middle items-center mx-auto text-2xl font-semibold mt-4">{editMode ? 'Actualiza tu movimiento' : 'Crear un movimiento'}</h1>
            <div className="flex flex-col mx-auto  w-full sm:w-4/5 items-center">
                <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 m-4 w-3/4">
                    <section className="flex flex-col flex-wrap w-3/4 mx-auto">
                      <label htmlFor="type"className="block text-gray-700 text-sm font-bold mb-2">Tipo de Movimiento</label>
                            <select 
                                name="type"
                                value={formData.type}
                                onChange={handleChange}
                                required={true}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                                <option value={'disable-value'} disabled> -- Selecciona una opcion -- </option>
                                <option value="expense">Remisiones</option>
                                <option value="income" >Depositos</option>
                            </select>
                            <label htmlFor="category"className="block text-gray-700 text-sm font-bold mb-2">Categoria</label>
                            <select 
                                name="category"
                                value={formData.category}
                                onChange={handleChange}
                                required={true}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                                <option value={'disable-value'} disabled> -- Selecciona una opcion -- </option>
                                <option value="nutricionales">Remision Nutricionales</option>
                                <option value="cosmeticos" >Remision Cosmeticos</option>
                            </select>
                            <label htmlFor="clientName" className="block text-gray-700 text-sm font-bold mb-2">Nombre del Cliente</label>
                            <input 
                            id="clientName"
                            name="clientName"
                            type="text"
                            onChange={handleChange}
                            required={true}
                            value={formData.clientName}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                            {/* Campo para seleccionar productos */}
                            <label htmlFor="product" className="block text-gray-700 text-sm font-bold mb-2">
                            Producto
                            </label>
                            <select
                                name="product"
                                onChange={(e) => setFormData({ ...formData, selectedProduct: e.target.value })}
                                required={true}
                                defaultValue={'disable-value'}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                                <option value={'disable-value'} disabled>-- Selecciona un producto --</option>
                                {productsList.map((product) => (
                                    <option key={product.id} value={product.id}>
                                    {product.name}
                                    </option>
                                ))}
                            </select>
                            <label htmlFor="quantity" className="block text-gray-700 text-sm font-bold mb-2">
                            Cantidad
                            </label>
                            <input
                                id="quantity"
                                name="quantity"
                                type="number"
                                onChange={(e) => setFormData({ ...formData, quantity: parseInt(e.target.value, 10) })}
                                required={true}
                                value={formData.quantity}
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                            <label htmlFor="amount" className="block text-gray-700 text-sm font-bold mb-2">Monto</label>
                            <input 
                            id="amount"
                            name="amount"
                            type="number"
                            readOnly
                            value={formData.products.reduce((total, product) => total + (product.price * product.quantity), 0)}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                            <button
                                onClick={() => handleAddProduct(formData.selectedProduct, formData.quantity)}
                                className="bg-yellow-400 hover:bg-yellow-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline cursor-pointer mt-3"
                                >
                                Agregar Producto
                            </button>
                            {/* Mostrar lista de productos en el ticket */}
                                <div>
                                <h2>Productos en el Ticket:</h2>
                                <ul>
                                    {formData.products.map((product) => (
                                    <li key={product.id}>
                                        {product.name} - Cantidad: {product.quantity} - Precio: {product.price * product.quantity}
                                    </li>
                                    ))}
                                </ul>
                                {/* Mostrar el monto total */}
                                <h3>Monto Total: {calculateTotalAmount()}</h3>
                            </div>
                            <label htmlFor="concept" className="block text-gray-700 text-sm font-bold mb-2">Concepto</label>
                            <input 
                            id="concept"
                            name="concept"
                            type="text"
                            onChange={handleChange}
                            required={true}
                            value={formData.concept}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                            <label htmlFor="date" className="block text-gray-700 text-sm font-bold mb-2">Fecha</label>
                            <input 
                            id="date"
                            name="date"
                            type="date"
                            onChange={handleChange}
                            required={true}
                            value={formData.date}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            />
                        <input value="Enviar" type="submit" className="bg-yellow-400 hover:bg-yellow-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline cursor-pointer mt-9" />
                    </section>
                </form>
            </div>
        </div>
    )
    
}
export default TicketPage
