import React from 'react';
import { Document, Page, Text} from '@react-pdf/renderer';


const TicketPDF = ({clientDetails, formData, objetoEnviado}) => {
  return (
    <Document>
        <Page size="A4" style={{paddingHorizontal: "50px", paddingVertical: "50px" }}>
            <Text style={{textAlign: 'right', fontSize: '22px' }}>{formData.date}</Text>
            <Text style={{textAlign: 'center',fontSize: '22px' }}>{`Remisión ${formData.ticketNumber}`}</Text>
            <div>
                <Text style={{fontSize: '12px', marginTop: "20px"}}>{`Nombre del cliente: ${formData.clientName}`}</Text>
                <Text style={{fontSize: '12px', paddingRight: "40px" }}>{`Dirección de envío: ${clientDetails.direction}`}</Text>
                <Text style={{fontSize: '12px', marginBottom: "20px" }}>{`Teléfono: ${clientDetails.phone}`}</Text>
            </div>
            <Text style={{textAlign: 'center', fontSize: '22px' }}>Detalle de productos:</Text>
            {objetoEnviado.map((objeto, index) => (
                <div key={index}>
                {/* Muestra los detalles de cada objeto del array */}
                <Text>{`Producto ${index + 1}`}</Text>
                <div style={{ display: 'flex', flexDirection: 'row' }}>
                    <Text>Cantidad</Text>
                    <Text>Precio</Text>
                    <Text>Nombre del Producto</Text>
                </div>
                <div style={{ display: 'flex', flexDirection: 'row' }}>
                    {Object.entries(objeto).map(([key, value], i) => {
                        let displayedKey = '';
                        switch (key) {
                        case 'cantidadProducto':
                            displayedKey = 'Cantidad';
                            break;
                        case 'nombreProducto':
                            displayedKey = 'Nombre';
                            break;
                        case 'precioProducto':
                            displayedKey = 'Precio';
                            break;
                        default:
                            displayedKey = key;
                            break;
                        }
                        return <Text key={i} style={{fontSize: '15px' }}>{`${displayedKey}: ${value}`  }</Text>;
                    })}
                </div>
                </div>
            ))}
            <Text>{`Remision: ${formData.amount}`}</Text>
        </Page>
    </Document>
  )
}

export default TicketPDF
